mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmnc */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_PRCS_SE_CD AS FCLT_PRCS_SE_CD , FCLT.FCLT_PRCS_TRMT_CD AS FCLT_PRCS_TRMT_CD , FCLT.FCLT_PRCS_TRMT_AMT AS FCLT_PRCS_TRMT_AMT , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_PRCS_PRFMNC FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmncList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_PRCS_SE_CD AS FCLT_PRCS_SE_CD , FCLT.FCLT_PRCS_TRMT_CD AS FCLT_PRCS_TRMT_CD , FCLT.FCLT_PRCS_TRMT_AMT AS FCLT_PRCS_TRMT_AMT , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_PRCS_PRFMNC FCLT WHERE 1=1 AND TRGT_YR = #{trgtYr} AND APC_CD = #{apcCd}
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

 	INSERT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.insertFcltPrcsPrfmnc */
 	  INTO TB_FCLT_PRCS_PRFMNC FCLT
 		(
 			TRGT_YR
 		  , APC_CD
 		  , SN
 		  , FCLT_PRCS_SE_CD
 		  , FCLT_PRCS_TRMT_CD
 		  , FCLT_PRCS_TRMT_AMT
 		  , DEL_YN
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
           , SYS_LAST_CHG_DT
           , SYS_LAST_CHG_USER_ID
           , SYS_LAST_CHG_PRGRM_ID
 		)
		VALUES
			(
			    #{trgtYr}
			  , #{apcCd}
			  , #{sn}
			  , #{fcltPrcsSeCd}
			  , #{fcltPrcsTrmtCd}
			  , #{fcltPrcsTrmtAmt}
			  , #{delYn}
			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)

mapper.update:

    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.updateFcltPrcsPrfmnc */
    		TB_FCLT_PRCS_PRFMNC FCLT
		   SET
		 	  TRGT_YR             = #{trgtYr}
		 	, APC_CD              = #{apcCd}
		 	, SN                  = #{sn}
		 	, FCLT_PRCS_SE_CD     = #{fcltPrcsSeCd}
		 	, FCLT_PRCS_TRMT_CD   = #{fcltPrcsTrmtCd}
		 	, FCLT_PRCS_TRMT_AMT  = #{fcltPrcsTrmtAmt}


			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.deleteFcltPrcsPrfmnc */
    		TB_FCLT_PRCS_PRFMNC FCLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
