mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrgrsMapper.selectPrgrs */ AP.CRTR_YR , AP.APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = AP.APC_CD) AS APC_NM , AP.PRGRS_SE_CD , AP.PRGRS_1 , AP.PRGRS_2 , AP.PRGRS_3 , AP.PRGRS_4 , AP.PRGRS_5 , AP.PRGRS_6 , AP.PRGRS_7 , AP.PRGRS_8 , AP.PRGRS_9 , AP.PRGRS_10 , AP.PRGRS_11 , AP.PRGRS_12 , AP.PRGRS_13 , AP.PRGRS_14 , AP.PRGRS_LAST , (SELECT COUNT(VAL) FROM TB_TE_APC_PRGRS UNPIVOT (VAL FOR INFO IN (PRGRS_1, PRGRS_2, PRGRS_3, PRGRS_4, PRGRS_5, PRGRS_6, PRGRS_7, PRGRS_8, PRGRS_9, PRGRS_10, PRGRS_11, PRGRS_12, PRGRS_13, PRGRS_14)) WHERE 1=1 AND VAL = 'Y' AND CRTR_YR = AP.CRTR_YR AND APC_CD = AP.APC_CD ) AS CNT FROM TB_TE_APC_PRGRS AP WHERE 1=1 AND AP.DEL_YN = 'N' AND AP.APC_CD IS NOT NULL AND AP.PRGRS_SE_CD = 'FCLT'
AND AP.CRTR_YR = #{crtrYr}
AND AP.APC_CD = #{apcCd}
AND AP.APC_CD IN (SELECT APC_CD FROM TB_APC_INFO WHERE APC_NM LIKE '%' || #{apcNm} || '%')
AND EXISTS(SELECT 1 FROM TB_TE_APC_OPER_TRMT_ITEM WHERE 1=1 AND APC_CD = AP.APC_CD AND OGNZ_SE_CD = '2' AND ITEM_NM LIKE '%' || #{itemNm} || '%' )

mapper.update:
MERGE INTO TB_TE_APC_PRGRS AP	/* com.at.apcss.fm.fclt.mapper.FcltPrgrsMapper.insertFcltPrgrs */
USING DUAL
ON (
	AP.CRTR_YR 		= #{crtrYr}
	AND AP.APC_CD 	= #{apcCd}
	AND AP.PRGRS_SE_CD = 'FCLT'
	)
WHEN MATCHED THEN
  UPDATE SET
		SYS_LAST_CHG_DT		=  SYSDATE
		, SYS_LAST_CHG_USER_ID	=  #{sysLastChgUserId}
		, SYS_LAST_CHG_PRGRM_ID	=  #{sysLastChgPrgrmId}

		, PRGRS_1			= #{prgrsVal}


		, PRGRS_2			= #{prgrsVal}


		, PRGRS_3			= #{prgrsVal}


		, PRGRS_4			= #{prgrsVal}


		, PRGRS_5			= #{prgrsVal}


		, PRGRS_6			= #{prgrsVal}


		, PRGRS_7			= #{prgrsVal}


		, PRGRS_8			= #{prgrsVal}


		, PRGRS_9			= #{prgrsVal}


		, PRGRS_10			= #{prgrsVal}


		, PRGRS_11			= #{prgrsVal}


		, PRGRS_12			= #{prgrsVal}


		, PRGRS_13			= #{prgrsVal}


		, PRGRS_14			= #{prgrsVal}


		, PRGRS_LAST		= #{prgrsLast}

WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , PRGRS_SE_CD


		  , PRGRS_1


		  , PRGRS_2


		  , PRGRS_3


		  , PRGRS_4


		  , PRGRS_5


		  , PRGRS_6


		  , PRGRS_7


		  , PRGRS_8


		  , PRGRS_9


		  , PRGRS_10


		  , PRGRS_11


		  , PRGRS_12


		  , PRGRS_13


		  , PRGRS_14


		  , PRGRS_LAST


		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , 'FCLT'


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsVal}


		 , #{prgrsLast}


		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)

UPDATE
	TB_TE_APC_PRGRS
SET
	PRGRS_LAST 				= #{prgrsLast}
	, LAST_USER_ID			= #{sysLastChgUserId}
	, LAST_DT				= SYSDATE

	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHERE 1=1
  AND CRTR_YR		= #{crtrYr}
  AND APC_CD		= #{apcCd}
  AND PRGRS_SE_CD	= 'FCLT'

UPDATE
	TB_TE_APC_PRGRS
SET

	SYS_LAST_CHG_DT			=  SYSDATE
	, SYS_LAST_CHG_USER_ID	=  #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	=  #{sysLastChgPrgrmId}


		, APRV_CTPV_STTS = #{aprvYn}
		, APRV_CTPV_NM = (SELECT USER_ID FROM TB_COM_USER WHERE USER_ID = #{userId})
		, APRV_CTPV_USER_ID = #{userId}
		, APRV_CTPV_DT = SYSDATE


		, APRV_CTPV_STTS = #{aprvYn}
		, RJCT_CTPV_NM = (SELECT USER_ID FROM TB_COM_USER WHERE USER_ID = #{userId})
		, RJCT_CTPV_USER_ID = #{userId}
		, RJCT_CTPV_DT = SYSDATE


		, APRV_CTPV_STTS = null




		, APRV_SGG_STTS = #{aprvYn}
		, APRV_SGG_NM = (SELECT USER_ID FROM TB_COM_USER WHERE USER_ID = #{userId})
		, APRV_SGG_USER_ID = #{userId}
		, APRV_SGG_DT = SYSDATE


		, APRV_SGG_STTS = #{aprvYn}
		, RJCT_SGG_NM = (SELECT USER_ID FROM TB_COM_USER WHERE USER_ID = #{userId})
		, RJCT_SGG_USER_ID = #{userId}
		, RJCT_SGG_DT = SYSDATE


		, APRV_SGG_STTS = null


WHERE 1=1
  AND CRTR_YR		= #{crtrYr}
  AND APC_CD		= #{apcCd}
  AND PRGRS_SE_CD	= 'FCLT'
