mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.selectFcltSortMchnInfo */ FCLT.TRGT_YR AS TRGT_YR ,FCLT.APC_CD AS APC_CD ,FCLT.SORT_MCHN_HLD_YN1 AS SORT_MCHN_HLD_YN1 ,FCLT.SORT_MCHN_HLD_YN2 AS SORT_MCHN_HLD_YN2 ,FCLT.SORT_MCHN_HLD_YN3 AS SORT_MCHN_HLD_YN3 ,FCLT.SORT_MCHN_HLD_YN4 AS SORT_MCHN_HLD_YN4 ,FCLT.PRC_CAP1 AS PRC_CAP1 ,FCLT.PRC_CAP2 AS PRC_CAP2 ,FCLT.PRC_CAP3 AS PRC_CAP3 ,FCLT.PRC_CAP4 AS PRC_CAP4 ,FCLT.PRC_VOL1 AS PRC_VOL1 ,FCLT.PRC_VOL2 AS PRC_VOL2 ,FCLT.PRC_VOL3 AS PRC_VOL3 ,FCLT.PRC_VOL4 AS PRC_VOL4 ,FCLT.ANN_OPER_TIME1 AS ANN_OPER_TIME1 ,FCLT.ANN_OPER_TIME2 AS ANN_OPER_TIME2 ,FCLT.ANN_OPER_TIME3 AS ANN_OPER_TIME3 ,FCLT.ANN_OPER_TIME4 AS ANN_OPER_TIME4 ,AVG_OPER_DAY1 AS AVG_OPER_DAY1 ,AVG_OPER_DAY2 AS AVG_OPER_DAY2 ,AVG_OPER_DAY3 AS AVG_OPER_DAY3 ,AVG_OPER_DAY4 AS AVG_OPER_DAY4
FROM TB_FCLT_SORT_MCHN_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.selectFcltSortMchnInfoList */ SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.selectFcltSortMchnInfo */ FCLT.TRGT_YR AS TRGT_YR ,FCLT.APC_CD AS APC_CD ,FCLT.SORT_MCHN_HLD_YN1 AS SORT_MCHN_HLD_YN1 ,FCLT.SORT_MCHN_HLD_YN2 AS SORT_MCHN_HLD_YN2 ,FCLT.SORT_MCHN_HLD_YN3 AS SORT_MCHN_HLD_YN3 ,FCLT.SORT_MCHN_HLD_YN4 AS SORT_MCHN_HLD_YN4 ,FCLT.PRC_CAP1 AS PRC_CAP1 ,FCLT.PRC_CAP2 AS PRC_CAP2 ,FCLT.PRC_CAP3 AS PRC_CAP3 ,FCLT.PRC_CAP4 AS PRC_CAP4 ,FCLT.PRC_VOL1 AS PRC_VOL1 ,FCLT.PRC_VOL2 AS PRC_VOL2 ,FCLT.PRC_VOL3 AS PRC_VOL3 ,FCLT.PRC_VOL4 AS PRC_VOL4 ,FCLT.ANN_OPER_TIME1 AS ANN_OPER_TIME1 ,FCLT.ANN_OPER_TIME2 AS ANN_OPER_TIME2 ,FCLT.ANN_OPER_TIME3 AS ANN_OPER_TIME3 ,FCLT.ANN_OPER_TIME4 AS ANN_OPER_TIME4 ,AVG_OPER_DAY1 AS AVG_OPER_DAY1 ,AVG_OPER_DAY2 AS AVG_OPER_DAY2 ,AVG_OPER_DAY3 AS AVG_OPER_DAY3 ,AVG_OPER_DAY4 AS AVG_OPER_DAY4 FROM TB_FCLT_SORT_MCHN_INFO FCLT WHERE 1=1 AND TRGT_YR = #{trgtYr} AND APC_CD = #{apcCd}
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

