mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.selectFcltSortMchnInfo */ OTI.CRTR_YR , OTI.APC_CD , OTI.APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM , OASM.SORT_ITEM_CD , OASM.SORT_ITEM_NM , OASM.SORT_PRCS_ABLT , OASM.SORT_PRCS_PRFMNC , OASM.SORT_OPRTNG_HR , OASM.SORT_AVG_OPRTNG_DCNT , OASM.SORT_MCHN_HOLD_YN
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_OPER_ABLT_SORT_MCHN OASM WHERE 1=1 AND OTI.APC_CD = OASM.APC_CD(+) AND OTI.CRTR_YR = OASM.CRTR_YR(+) AND OTI.SN = OASM.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.selectFcltSortMchnInfoList */ OTI.CRTR_YR , OTI.APC_CD , OTI.APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM , OASM.SORT_ITEM_CD , OASM.SORT_ITEM_NM , OASM.SORT_PRCS_ABLT , OASM.SORT_PRCS_PRFMNC , OASM.SORT_OPRTNG_HR , OASM.SORT_AVG_OPRTNG_DCNT , OASM.SORT_MCHN_HOLD_YN
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_OPER_ABLT_SORT_MCHN OASM WHERE 1=1 AND OTI.APC_CD = OASM.APC_CD(+) AND OTI.CRTR_YR = OASM.CRTR_YR(+) AND OTI.SN = OASM.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_OPER_ABLT_SORT_MCHN OASM /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.insertFcltSortMchnInfo */
USING DUAL
ON (
	OASM.CRTR_YR = #{crtrYr}
	AND OASM.APC_CD = #{apcCd}
	AND OASM.SN = #{sn}
	)
WHEN MATCHED THEN
  UPDATE SET
		  SORT_PRCS_ABLT			= #{sortPrcsAblt}
		  , SORT_PRCS_PRFMNC		= #{sortPrcsPrfmnc}
		  , SORT_OPRTNG_HR			= #{sortOprtngHr}
		  , SORT_AVG_OPRTNG_DCNT	= #{sortAvgOprtngDcnt}

		  , SORT_MCHN_HOLD_YN		= #{sortMchnHoldYn}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		   , APC_CD
		   , SN
		   , SORT_ITEM_CD
		   , SORT_ITEM_NM

		   , SORT_PRCS_ABLT
		   , SORT_PRCS_PRFMNC
		   , SORT_OPRTNG_HR
		   , SORT_AVG_OPRTNG_DCNT

		   , SORT_MCHN_HOLD_YN

		   , SYS_FRST_INPT_DT
		   , SYS_FRST_INPT_USER_ID
		   , SYS_FRST_INPT_PRGRM_ID
		   , SYS_LAST_CHG_DT
		   , SYS_LAST_CHG_USER_ID
		   , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
			#{crtrYr}
			, #{apcCd}
			, #{sn}
			,(SELECT ITEM_CD FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
			,(SELECT ITEM_NM FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')

			, #{sortPrcsAblt}
			, #{sortPrcsPrfmnc}
			, #{sortOprtngHr}
			, #{sortAvgOprtngDcnt}

			, #{sortMchnHoldYn}

			, SYSDATE
			, #{sysFrstInptUserId}
			, #{sysFrstInptPrgrmId}
			, SYSDATE
			, #{sysLastChgUserId}
			, #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltSortMchnInfoMapper.updateFcltSortMchnInfo */
	TB_TE_APC_OPER_ABLT_SORT_MCHN OASM
   SET
		SORT_PRCS_ABLT			= #{sortPrcsAblt}
	  , SORT_PRCS_PRFMNC		= #{sortPrcsPrfmnc}
	  , SORT_OPRTNG_HR			= #{sortOprtngHr}
	  , SORT_AVG_OPRTNG_DCNT	= #{sortAvgOprtngDcnt}

	  , SORT_MCHN_HOLD_YN		= #{sortMchnHoldYn}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND  APC_CD = #{apcCd}
