mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.selectFcltSortMchnOperInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD ,(SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.SORT_MCHN_HLD_YN_AYR1 AS SORT_MCHN_HLD_YN_AYR1 , FCLT.SORT_MCHN_HLD_YN_101 AS SORT_MCHN_HLD_YN101 , FCLT.SORT_MCHN_HLD_YN_102 AS SORT_MCHN_HLD_YN_102 , FCLT.SORT_MCHN_HLD_YN_103 AS SORT_MCHN_HLD_YN_103 , FCLT.SORT_MCHN_HLD_YN_104 AS SORT_MCHN_HLD_YN_104 , FCLT.SORT_MCHN_HLD_YN_105 AS SORT_MCHN_HLD_YN_105 , FCLT.SORT_MCHN_HLD_YN_106 AS SORT_MCHN_HLD_YN_106 , FCLT.SORT_MCHN_HLD_YN_107 AS SORT_MCHN_HLD_YN_107 , FCLT.SORT_MCHN_HLD_YN_108 AS SORT_MCHN_HLD_YN_108 , FCLT.SORT_MCHN_HLD_YN_109 AS SORT_MCHN_HLD_YN_109 , FCLT.SORT_MCHN_HLD_YN_110 AS SORT_MCHN_HLD_YN_110 , FCLT.SORT_MCHN_HLD_YN_111 AS SORT_MCHN_HLD_YN_111 , FCLT.SORT_MCHN_HLD_YN_112 AS SORT_MCHN_HLD_YN_112 , FCLT.SORT_MCHN_HLD_YN_AYR2 AS SORT_MCHN_HLD_YN_AYR2
FROM TB_FCLT_SORT_MCHN_OPER_INFO FCLT WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.selectFcltSortMchnOperInfoList */ CRTR_YR , APC_CD , SN , ITEM_CD , ITEM_NM , OPER_YN , OPER_PERIOD_YN_1 , OPER_PERIOD_YN_2 , OPER_PERIOD_YN_3 , OPER_PERIOD_YN_4 , OPER_PERIOD_YN_5 , OPER_PERIOD_YN_6 , OPER_PERIOD_YN_7 , OPER_PERIOD_YN_8 , OPER_PERIOD_YN_9 , OPER_PERIOD_YN_10 , OPER_PERIOD_YN_11 , OPER_PERIOD_YN_12
FROM TB_TE_APC_MCHN_OPER_PERIOD AMOP WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL AND MCHN_SE_CD = '1'
AND AMOP.CRTR_YR = #{crtrYr}
AND AMOP.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_MCHN_OPER_PERIOD AMOP /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.insertFcltSortMchnOperInfo */
USING DUAL
ON (
	AMOP.CRTR_YR = #{crtrYr}
	AND AMOP.APC_CD = #{apcCd}
	AND AMOP.SN = #{sn}
	AND AMOP.MCHN_SE_CD = '1'
	)
WHEN MATCHED THEN
  UPDATE SET
		  OPER_YN				= #{operYn}
		  , OPER_PERIOD_YN_1	= #{operPeriodYn1}
		  , OPER_PERIOD_YN_2	= #{operPeriodYn2}
		  , OPER_PERIOD_YN_3	= #{operPeriodYn3}
		  , OPER_PERIOD_YN_4	= #{operPeriodYn4}
		  , OPER_PERIOD_YN_5	= #{operPeriodYn5}
		  , OPER_PERIOD_YN_6	= #{operPeriodYn6}
		  , OPER_PERIOD_YN_7	= #{operPeriodYn7}
		  , OPER_PERIOD_YN_8	= #{operPeriodYn8}
		  , OPER_PERIOD_YN_9	= #{operPeriodYn9}
		  , OPER_PERIOD_YN_10	= #{operPeriodYn10}
		  , OPER_PERIOD_YN_11	= #{operPeriodYn11}
		  , OPER_PERIOD_YN_12	= #{operPeriodYn12}

		  , SYS_LAST_CHG_DT		= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		CRTR_YR
		, APC_CD
		, SN
		, ITEM_CD
		, ITEM_NM
		, MCHN_SE_CD
		, OPER_YN
		, OPER_PERIOD_YN_1
		, OPER_PERIOD_YN_2
		, OPER_PERIOD_YN_3
		, OPER_PERIOD_YN_4
		, OPER_PERIOD_YN_5
		, OPER_PERIOD_YN_6
		, OPER_PERIOD_YN_7
		, OPER_PERIOD_YN_8
		, OPER_PERIOD_YN_9
		, OPER_PERIOD_YN_10
		, OPER_PERIOD_YN_11
		, OPER_PERIOD_YN_12

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_USER_ID
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_USER_ID
		, SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , #{sn}
		  , (SELECT ITEM_CD FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  , (SELECT ITEM_NM FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  , '1'
		  , #{operYn}
		  , #{operPeriodYn1}
		  , #{operPeriodYn2}
		  , #{operPeriodYn3}
		  , #{operPeriodYn4}
		  , #{operPeriodYn5}
		  , #{operPeriodYn6}
		  , #{operPeriodYn7}
		  , #{operPeriodYn8}
		  , #{operPeriodYn9}
		  , #{operPeriodYn10}
		  , #{operPeriodYn11}
		  , #{operPeriodYn12}

		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.updateFcltSortMchnOperInfo */
	TB_TE_APC_MCHN_OPER_PERIOD AMOP
   SET
	OPER_YN				= #{operYn}
	  , OPER_PERIOD_YN_1	= #{operPeriodYn1}
	  , OPER_PERIOD_YN_2	= #{operPeriodYn2}
	  , OPER_PERIOD_YN_3	= #{operPeriodYn3}
	  , OPER_PERIOD_YN_4	= #{operPeriodYn4}
	  , OPER_PERIOD_YN_5	= #{operPeriodYn5}
	  , OPER_PERIOD_YN_6	= #{operPeriodYn6}
	  , OPER_PERIOD_YN_7	= #{operPeriodYn7}
	  , OPER_PERIOD_YN_8	= #{operPeriodYn8}
	  , OPER_PERIOD_YN_9	= #{operPeriodYn9}
	  , OPER_PERIOD_YN_10	= #{operPeriodYn10}
	  , OPER_PERIOD_YN_11	= #{operPeriodYn11}
	  , OPER_PERIOD_YN_12	= #{operPeriodYn12}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
  AND MCHN_SE_CD = '1'


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.deleteFcltSortMchnOperInfo */
	TB_TE_APC_MCHN_OPER_PERIOD AMOP
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
  AND MCHN_SE_CD = '1'
