mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.selectFcltSortMchnOperInfoList */ OTI.CRTR_YR , OTI.APC_CD , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM , AMOP.OPER_YN , AMOP.OPER_PERIOD_YN_1 , AMOP.OPER_PERIOD_YN_2 , AMOP.OPER_PERIOD_YN_3 , AMOP.OPER_PERIOD_YN_4 , AMOP.OPER_PERIOD_YN_5 , AMOP.OPER_PERIOD_YN_6 , AMOP.OPER_PERIOD_YN_7 , AMOP.OPER_PERIOD_YN_8 , AMOP.OPER_PERIOD_YN_9 , AMOP.OPER_PERIOD_YN_10 , AMOP.OPER_PERIOD_YN_11 , AMOP.OPER_PERIOD_YN_12
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,(SELECT * FROM TB_TE_APC_MCHN_OPER_PERIOD WHERE MCHN_SE_CD = '1' ) AMOP WHERE 1=1 AND OTI.APC_CD = AMOP.APC_CD(+) AND OTI.CRTR_YR = AMOP.CRTR_YR(+) AND OTI.SN = AMOP.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.selectFcltSortMchnOperInfoList */ OTI.CRTR_YR , OTI.APC_CD , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM , AMOP.OPER_YN , AMOP.OPER_PERIOD_YN_1 , AMOP.OPER_PERIOD_YN_2 , AMOP.OPER_PERIOD_YN_3 , AMOP.OPER_PERIOD_YN_4 , AMOP.OPER_PERIOD_YN_5 , AMOP.OPER_PERIOD_YN_6 , AMOP.OPER_PERIOD_YN_7 , AMOP.OPER_PERIOD_YN_8 , AMOP.OPER_PERIOD_YN_9 , AMOP.OPER_PERIOD_YN_10 , AMOP.OPER_PERIOD_YN_11 , AMOP.OPER_PERIOD_YN_12
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,(SELECT * FROM TB_TE_APC_MCHN_OPER_PERIOD WHERE MCHN_SE_CD = '1' ) AMOP WHERE 1=1 AND OTI.APC_CD = AMOP.APC_CD(+) AND OTI.CRTR_YR = AMOP.CRTR_YR(+) AND OTI.SN = AMOP.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_MCHN_OPER_PERIOD AMOP /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.insertFcltSortMchnOperInfo */
USING DUAL
ON (
	AMOP.CRTR_YR = #{crtrYr}
	AND AMOP.APC_CD = #{apcCd}
	AND AMOP.SN = #{sn}
	AND AMOP.MCHN_SE_CD = '1'
	)
WHEN MATCHED THEN
  UPDATE SET
		  OPER_YN				= #{operYn}
		  , OPER_PERIOD_YN_1	= #{operPeriodYn1}
		  , OPER_PERIOD_YN_2	= #{operPeriodYn2}
		  , OPER_PERIOD_YN_3	= #{operPeriodYn3}
		  , OPER_PERIOD_YN_4	= #{operPeriodYn4}
		  , OPER_PERIOD_YN_5	= #{operPeriodYn5}
		  , OPER_PERIOD_YN_6	= #{operPeriodYn6}
		  , OPER_PERIOD_YN_7	= #{operPeriodYn7}
		  , OPER_PERIOD_YN_8	= #{operPeriodYn8}
		  , OPER_PERIOD_YN_9	= #{operPeriodYn9}
		  , OPER_PERIOD_YN_10	= #{operPeriodYn10}
		  , OPER_PERIOD_YN_11	= #{operPeriodYn11}
		  , OPER_PERIOD_YN_12	= #{operPeriodYn12}

		  , SYS_LAST_CHG_DT		= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		CRTR_YR
		, APC_CD
		, SN
		, ITEM_CD
		, ITEM_NM
		, MCHN_SE_CD
		, OPER_YN
		, OPER_PERIOD_YN_1
		, OPER_PERIOD_YN_2
		, OPER_PERIOD_YN_3
		, OPER_PERIOD_YN_4
		, OPER_PERIOD_YN_5
		, OPER_PERIOD_YN_6
		, OPER_PERIOD_YN_7
		, OPER_PERIOD_YN_8
		, OPER_PERIOD_YN_9
		, OPER_PERIOD_YN_10
		, OPER_PERIOD_YN_11
		, OPER_PERIOD_YN_12

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_USER_ID
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_USER_ID
		, SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , #{sn}
		  , (SELECT ITEM_CD FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  , (SELECT ITEM_NM FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  , '1'
		  , #{operYn}
		  , #{operPeriodYn1}
		  , #{operPeriodYn2}
		  , #{operPeriodYn3}
		  , #{operPeriodYn4}
		  , #{operPeriodYn5}
		  , #{operPeriodYn6}
		  , #{operPeriodYn7}
		  , #{operPeriodYn8}
		  , #{operPeriodYn9}
		  , #{operPeriodYn10}
		  , #{operPeriodYn11}
		  , #{operPeriodYn12}

		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.updateFcltSortMchnOperInfo */
	TB_TE_APC_MCHN_OPER_PERIOD AMOP
   SET
	OPER_YN				= #{operYn}
	  , OPER_PERIOD_YN_1	= #{operPeriodYn1}
	  , OPER_PERIOD_YN_2	= #{operPeriodYn2}
	  , OPER_PERIOD_YN_3	= #{operPeriodYn3}
	  , OPER_PERIOD_YN_4	= #{operPeriodYn4}
	  , OPER_PERIOD_YN_5	= #{operPeriodYn5}
	  , OPER_PERIOD_YN_6	= #{operPeriodYn6}
	  , OPER_PERIOD_YN_7	= #{operPeriodYn7}
	  , OPER_PERIOD_YN_8	= #{operPeriodYn8}
	  , OPER_PERIOD_YN_9	= #{operPeriodYn9}
	  , OPER_PERIOD_YN_10	= #{operPeriodYn10}
	  , OPER_PERIOD_YN_11	= #{operPeriodYn11}
	  , OPER_PERIOD_YN_12	= #{operPeriodYn12}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
  AND MCHN_SE_CD = '1'


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltSortMchnOperInfoMapper.deleteFcltSortMchnOperInfo */
	TB_TE_APC_MCHN_OPER_PERIOD AMOP
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
  AND MCHN_SE_CD = '1'
