mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ ASPS.CRTR_YR , ASPS.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = ASPS.APC_CD AND CRTR_YR = ASPS.CRTR_YR ) AS APC_NM
, UMS_PRDCTN_INFO , UMS_WRHS_INFO , UMS_SORT_INFO , UMS_STRG_INFO , UMS_PCKG_INFO , UMS_SPMT_INFO , UMS_CLCLN_INFO , UMS_WGH_INFO
FROM TB_TE_APC_SMART_PRPLSN_STTN ASPS WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND ASPS.CRTR_YR = #{crtrYr}
AND ASPS.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ ASPS.CRTR_YR , ASPS.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = ASPS.APC_CD AND CRTR_YR = ASPS.CRTR_YR ) AS APC_NM
, UMS_PRDCTN_INFO , UMS_WRHS_INFO , UMS_SORT_INFO , UMS_STRG_INFO , UMS_PCKG_INFO , UMS_SPMT_INFO , UMS_CLCLN_INFO , UMS_WGH_INFO
FROM TB_TE_APC_SMART_PRPLSN_STTN ASPS WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND ASPS.CRTR_YR = #{crtrYr}
AND ASPS.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_SMART_PRPLSN_STTN ASPS /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.insertFcltUnityMngInfo */
USING DUAL
ON (
	ASPS.CRTR_YR = #{crtrYr}
	AND ASPS.APC_CD = #{apcCd}
	)
WHEN MATCHED THEN
  UPDATE SET
		  UMS_PRDCTN_INFO = #{umsPrdctnInfo}
		  , UMS_WRHS_INFO = #{umsWrhsInfo}
		  , UMS_SORT_INFO = #{umsSortInfo}
		  , UMS_STRG_INFO = #{umsStrgInfo}
		  , UMS_PCKG_INFO = #{umsPckgInfo}
		  , UMS_SPMT_INFO = #{umsSpmtInfo}
		  , UMS_CLCLN_INFO = #{umsClclnInfo}
		  , UMS_WGH_INFO = #{umsWghInfo}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM

		  , UMS_PRDCTN_INFO
		  , UMS_WRHS_INFO
		  , UMS_SORT_INFO
		  , UMS_STRG_INFO
		  , UMS_PCKG_INFO
		  , UMS_SPMT_INFO
		  , UMS_CLCLN_INFO
		  , UMS_WGH_INFO

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})

		  , #{umsPrdctnInfo}
		  , #{umsWrhsInfo}
		  , #{umsSortInfo}
		  , #{umsStrgInfo}
		  , #{umsPckgInfo}
		  , #{umsSpmtInfo}
		  , #{umsClclnInfo}
		  , #{umsWghInfo}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.updateFcltUnityMngInfo */
	TB_TE_APC_SMART_PRPLSN_STTN ASPS
   SET
	  UMS_PRDCTN_INFO = #{umsPrdctnInfo}
	  , UMS_WRHS_INFO = #{umsWrhsInfo}
	  , UMS_SORT_INFO = #{umsSortInfo}
	  , UMS_STRG_INFO = #{umsStrgInfo}
	  , UMS_PCKG_INFO = #{umsPckgInfo}
	  , UMS_SPMT_INFO = #{umsSpmtInfo}
	  , UMS_CLCLN_INFO = #{umsClclnInfo}
	  , UMS_WGH_INFO = #{umsWghInfo}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND ASPS.CRTR_YR = #{crtrYr}
  AND ASPS.APC_CD = #{apcCd}
