mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.selectFcltUnityMngInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_UNTY_MNG_INFO FCLT
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.selectFcltUnityMngInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_UNTY_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

 	INSERT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.insertFcltUnityMngInfo */
 	  INTO TB_FCLT_UNTY_MNG_INFO FCLT
 		(
