 		  , SN
 		  , FCLT_SYS_INSTL_CD
 		  , FCLT_SYS_HLD_YN
 		  , DEL_YN
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
 		  , SYS_LAST_CHG_DT
 		  , SYS_LAST_CHG_USER_ID
 		  , SYS_LAST_CHG_PRGRM_ID
 		)
		VALUES
			(
			    #{trgtYr}
			  , #{apcCd}
			  , #{sn}
			  , #{fcltSysInstlCd}
			  , #{fcltSysHldYn}
			  , #{delYn}
			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
		      , #{sysLastChgUserId}
		      , #{sysLastChgPrgrmId}
			)

mapper.update:

    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.updateFcltUnityMngInfo */
    		TB_FCLT_UNTY_MNG_INFO FCLT
		   SET
