		 	, APC_CD            = #{apcCd}
		 	, SN                = #{sn}
		 	, FCLT_SYS_INSTL_CD = #{fcltSysInstlCd}
		 	, FCLT_SYS_HLD_YN   = #{fcltSysHldYn}


			, DEL_YN 	= #{delYn}


