mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.selectFcltUnityMngInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_HLD_YN2 AS FCLT_SYS_HLD_YN2 , FCLT.FCLT_SYS_HLD_YN3 AS FCLT_SYS_HLD_YN3 , FCLT.FCLT_SYS_HLD_YN4 AS FCLT_SYS_HLD_YN4 , FCLT.FCLT_SYS_HLD_YN5 AS FCLT_SYS_HLD_YN5 , FCLT.FCLT_SYS_HLD_YN6 AS FCLT_SYS_HLD_YN6 , FCLT.FCLT_SYS_HLD_YN7 AS FCLT_SYS_HLD_YN7 FROM TB_FCLT_UNTY_MNG_INFO FCLT
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.selectFcltUnityMngInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_HLD_YN2 AS FCLT_SYS_HLD_YN2 , FCLT.FCLT_SYS_HLD_YN3 AS FCLT_SYS_HLD_YN3 , FCLT.FCLT_SYS_HLD_YN4 AS FCLT_SYS_HLD_YN4 , FCLT.FCLT_SYS_HLD_YN5 AS FCLT_SYS_HLD_YN5 , FCLT.FCLT_SYS_HLD_YN6 AS FCLT_SYS_HLD_YN6 , FCLT.FCLT_SYS_HLD_YN7 AS FCLT_SYS_HLD_YN7 FROM TB_FCLT_UNTY_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
     	MERGE INTO TB_FCLT_UNTY_MNG_INFO FCLT /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.insertFcltUnityMngInfo */
		USING DUAL
		ON (
			FCLT.TRGT_YR = #{trgtYr}
			AND FCLT.APC_CD = #{apcCd}
			)
		WHEN MATCHED THEN
		  UPDATE SET
				  FCLT_SYS_HLD_YN   = #{fcltSysHldYn}
				, FCLT_SYS_HLD_YN2 	= #{fcltSysHldYn2}
				, FCLT_SYS_HLD_YN3  = #{fcltSysHldYn3}
				, FCLT_SYS_HLD_YN4  = #{fcltSysHldYn4}
				, FCLT_SYS_HLD_YN5  = #{fcltSysHldYn5}
				, FCLT_SYS_HLD_YN6  = #{fcltSysHldYn6}
				, FCLT_SYS_HLD_YN7  = #{fcltSysHldYn7}
				  , SYS_LAST_CHG_DT		= SYSDATE
	    		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
		WHEN NOT MATCHED THEN
		  INSERT (
				  TRGT_YR
	    		  , APC_CD
	    		  , FCLT_SYS_HLD_YN
	    		  , FCLT_SYS_HLD_YN2
	    		  , FCLT_SYS_HLD_YN3
	    		  , FCLT_SYS_HLD_YN4
	    		  , FCLT_SYS_HLD_YN5
	    		  , FCLT_SYS_HLD_YN6
	    		  , FCLT_SYS_HLD_YN7
	    		  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	    		  , SYS_LAST_CHG_DT
	    		  , SYS_LAST_CHG_USER_ID
	    		  , SYS_LAST_CHG_PRGRM_ID
	    		)
	   	VALUES
	   		    (
	   			  #{trgtYr}
	   			  , #{apcCd}
	   			  , #{fcltSysHldYn}
				  , #{fcltSysHldYn2}
	   			  , #{fcltSysHldYn3}
	   			  , #{fcltSysHldYn4}
	   			  , #{fcltSysHldYn5}
	   			  , #{fcltSysHldYn6}
	   			  , #{fcltSysHldYn7}
	   			  , SYSDATE
	   			  , #{sysFrstInptUserId}
	   			  , #{sysFrstInptPrgrmId}
	   			  , SYSDATE
	   		      , #{sysLastChgUserId}
	   		      , #{sysLastChgPrgrmId}
		     	)


    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.updateFcltUnityMngInfo */
    		TB_FCLT_UNTY_MNG_INFO FCLT
		   SET
		 	FCLT_SYS_HLD_YN   = #{fcltSysHldYn}
			, FCLT_SYS_HLD_YN2 	= #{fcltSysHldYn2}
			, FCLT_SYS_HLD_YN3  = #{fcltSysHldYn3}
			, FCLT_SYS_HLD_YN4  = #{fcltSysHldYn4}
			, FCLT_SYS_HLD_YN5  = #{fcltSysHldYn5}
			, FCLT_SYS_HLD_YN6  = #{fcltSysHldYn6}
			, FCLT_SYS_HLD_YN7  = #{fcltSysHldYn7}



			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltUnityMngInfoMapper.deleteFcltUnityMngInfo */
    		TB_FCLT_UNTY_MNG_INFO FCLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
