SELECT pt0.CTPV_NM /* com.at.apcss.fm.fclt.mapper.MdRtlFcltMapper.selectMapSttn */ , CASE WHEN pt0.CTPV_CD = '11' THEN '11' WHEN pt0.CTPV_CD = '26' THEN '21' WHEN pt0.CTPV_CD = '27' THEN '22' WHEN pt0.CTPV_CD = '28' THEN '23' WHEN pt0.CTPV_CD = '29' THEN '24' WHEN pt0.CTPV_CD = '30' THEN '25' WHEN pt0.CTPV_CD = '31' THEN '26' WHEN pt0.CTPV_CD = '36' THEN '29' WHEN pt0.CTPV_CD = '41' THEN '31' WHEN pt0.CTPV_CD = '43' THEN '33' WHEN pt0.CTPV_CD = '44' THEN '34' WHEN pt0.CTPV_CD = '45' THEN '35' WHEN pt0.CTPV_CD = '46' THEN '36' WHEN pt0.CTPV_CD = '47' THEN '37' WHEN pt0.CTPV_CD = '48' THEN '38' WHEN pt0.CTPV_CD = '50' THEN '39' WHEN pt0.CTPV_CD = '51' THEN '32' END AS CTPV_CD , pt0.APC_QNTT FROM ( SELECT CTPV_NM , CTPV_CD , COUNT(*) APC_QNTT FROM TB_TE_APC_OPER_PRSN_OTLN WHERE CTPV_CD IS NOT NULL
AND CRTR_YR = #{crtrYr}
GROUP BY CTPV_CD, CTPV_NM ) pt0

SELECT /* com.at.apcss.fm.fclt.mapper.MdRtlFcltMapper.selectCtpvAreaLis */ AOO.CRTR_YR AS CRTR_YR , AOO.APC_CD AS APC_CD , AOO.APC_NM AS APC_NM , AOO.APC_BRNO AS APC_BRNO , AOO.OPER_OGNZ_CD AS OPER_OGNZ_CD , AOO.OPER_OGNZ_NM AS OPER_OGNZ_NM , AOO.OPER_OGNZ_BRNO AS OPER_OGNZ_BRNO , AOO.CTPV_CD AS CTPV_CD , AOO.CTPV_NM AS CTPV_NM , AOO.SIGUN_CD AS SIGUN_CD , AOO.SIGUN_NM AS SIGUN_NM , AOO.MB AS MB , AOO.OGNZ_TYPE_CD AS OGNZ_TYPE_CD , AOO.OGNZ_TYPE AS OGNZ_TYPE , AOO.TYPE_CD AS TYPE_CD , AOO.TYPE_NM AS TYPE_NM , AOO.OWNR AS OWNR , AOO.LOCTN AS LOCTN , AOO.RPRSV AS RPRSV , AOO.PIC AS PIC , AOO.PIC_JBPS AS PIC_JBPS , AOO.CTTPC AS CTTPC , AOO.EML AS EML , AOO.TOT_TRMT_AMT AS TOT_TRMT_AMT , AOO.GVRMNT_SPRT_YN AS GVRMNT_SPRT_YN , AOO.RMRK AS RMRK
FROM TB_TE_APC_OPER_PRSN_OTLN AOO WHERE 1=1
AND AOO.CRTR_YR = #{crtrYr}
AND AOO.CTPV_CD = #{ctpvCd}
ORDER BY AOO.APC_CD
