mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.mngmst.mapper.MngmstBscInfoMapper.selectMngmstBscInfoList */ CM.MSG_KEY AS MSG_KEY , CM.MSG_CN AS MSG_CN , CM.MSG_KND AS MSG_KND , (SELECT FN_GET_COM_CD_VL_NM('MSG_KND', CM.MSG_KND) FROM DUAL) AS MSG_KND_NM , CM.RMRK AS RMRK , CM.DEL_YN AS DEL_YN , CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_COM_MSG CM
AND CM.MSG_KEY LIKE #{msgKey} || '%'
AND CM.MSG_CN LIKE '%' || #{msgCn} || '%'
AND CM.MSG_KND = #{msgKnd}
AND CM.DEL_YN = #{delYn}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

 	INSERT /* com.at.apcss.fm.mngmst.mapper.MngmstBscInfoMapper.insertMngmstBscInfo */
 	  INTO TB_COM_MSG
 		(
 			MSG_KEY
 		  , MSG_CN
 		  , MSG_KND
 		  , RMRK
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
 		  , SYS_LAST_CHG_DT
 		  , SYS_LAST_CHG_USER_ID
 		  , SYS_LAST_CHG_PRGRM_ID
 		)
		VALUES
			(
			    #{msgKey}
			  , #{msgCn}
			  , #{msgKnd}
			  , #{rmrk}
			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)

mapper.update:

    	UPDATE /* com.at.apcss.fm.mngmst.mapper.MngmstBscInfoMapper.updateMngmstBscInfo */
    		TB_COM_MSG
		   SET
		 	  MSG_CN	= #{msgCn}
		 	, MSG_KND 	= #{msgKnd}
		 	, RMRK 		= #{rmrk}


			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.mngmst.mapper.MngmstBscInfoMapper.deleteMngmstBscInfo */
    		TB_COM_MSG
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
