mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.popup.mapper.GpcSelectPopupMapper.selectUoGpcList */ ITEM_NM ,ITEM_CD ,CTGRY_CD ,BRNO ,(SELECT CORP_NM FROM TB_EV_APO WHERE BRNO = IC.BRNO) AS CORP_NM FROM TB_EV_APO_YR_GPC_CD IC WHERE 1=1 AND BRNO IN (SELECT UO_BRNO FROM TB_EV_APO_UO_CD WHERE ISO_BRNO = #{brno}) AND STTG_UPBR_ITEM_SE IN ('1','2')
AND IC.ITEM_NM LIKE '%'||#{itemNm}||'%'
AND IC.YR = #{yr}
ORDER BY IC.ITEM_NM ASC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
