mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM
FROM ( SELECT /* com.at.apcss.fm.wrhs.mapper.RawMtrWrhsPrnmntMapper.selectRawMtrWrhsPrnmntList */ WP.APC_CD AS APC_CD , WP.PRDCR_CD AS PRDCR_CD , WP.PRNMNTNO AS PRNMNTNO , WP.PRNMNT_YMD AS PRNMNT_YMD , WP.PRNMNT_HR AS PRNMNT_HR , WP.ITEM_CD AS ITEM_CD , WP.VRTY_CD AS VRTY_CD , WP.PRNMNT_QNTT AS PRNMNT_QNTT , WP.PRNMNT_WGHT AS PRNMNT_WGHT , WP.RMRK AS RMRK
FROM TB_RAW_MTR_WRHS_PRNMNT WP
WHERE WP.DEL_YN = 'N'
AND WP.APC_CD = #{apcCd}
AND WP.PRNMNT_YMD >= #{wrhsPrnmntYmdFrom}
AND WP.PRNMNT_YMD <= #{wrhsPrnmntYmdTo}
AND WP.PRNMNT_YMD >= #{prnmntYmdFrom}
AND WP.PRNMNT_YMD <= #{prnmntYmdTo}
AND WP.PRDCR_CD = #{prdcrCd}
AND WP.ITEM_CD = #{itemCd}
AND WP.VRTY_CD = #{vrtyCd}
ORDER BY WP.APC_CD, PRNMNT_YMD DESC ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:

UPDATE /* com.at.apcss.fm.wrhs.mapper.RawMtrWrhsPrnmntMapper.updateRawMtrWrhsPrnmntDelYn */
    TB_RAW_MTR_WRHS_PRNMNT
   SET
      SYS_LAST_CHG_DT 		    = SYSDATE
    , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
    , DEL_YN                    = 'Y'

WHERE APC_CD   = #{apcCd}
  AND PRDCR_CD = #{prdcrCd}
  AND PRNMNTNO = #{prnmntno}

mapper.insert:

INSERT /* com.at.apcss.fm.wrhs.mapper.RawMtrWrhsPrnmntMapper.insertRawMtrWrhsPrnmnt */
INTO TB_RAW_MTR_WRHS_PRNMNT (
      APC_CD
    , PRDCR_CD
    , PRNMNTNO
    , PRNMNT_YMD
    , PRNMNT_HR
    , ITEM_CD
    , VRTY_CD
    , PRNMNT_QNTT
    , PRNMNT_WGHT
    , RMRK
    , SYS_FRST_INPT_DT
    , SYS_FRST_INPT_USER_ID
    , SYS_FRST_INPT_PRGRM_ID
    , SYS_LAST_CHG_DT
    , SYS_LAST_CHG_USER_ID
    , SYS_LAST_CHG_PRGRM_ID
) VALUES (
      #{apcCd}
    , #{prdcrCd}
    , #{prnmntno}
    , #{prnmntYmd}
    , #{prnmntHr}
    , #{itemCd}
    , #{vrtyCd}
    , #{prnmntQntt}
    , #{prnmntWght}
    , #{rmrk}
    , SYSDATE
    , #{sysFrstInptUserId}
    , #{sysFrstInptPrgrmId}
    , SYSDATE
    , #{sysLastChgUserId}
    , #{sysLastChgPrgrmId}
)

mapper.update:

UPDATE /* com.at.apcss.fm.wrhs.mapper.RawMtrWrhsPrnmntMapper.updateRawMtrWrhsPrnmnt */
    TB_RAW_MTR_WRHS_PRNMNT
   SET
   	  ITEM_CD					= #{itemCd}
   	, VRTY_CD					= #{vrtyCd}
   	, PRNMNT_HR					= #{prnmntHr}
   	, PRNMNT_YMD				= #{prnmntYmd}
   	, PRNMNT_QNTT				= #{prnmntQntt}
   	, PRNMNT_WGHT               = #{prnmntWght}
   	, RMRK                      = #{rmrk}
    , SYS_LAST_CHG_DT 		    = SYSDATE
    , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
WHERE APC_CD   = #{apcCd}
  AND PRDCR_CD = #{prdcrCd}
  AND PRNMNTNO = #{prnmntno}
