mapper.select:
SELECT /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.selectPrdcrCrclOgnReqMng */ CM.APO_CD , CM.APO_SE , CM.WRT_YN , CM.CORP_NM , CM.BRNO , CM.INVST_NOPE , CM.INVST_EXPND_FRMER_NOPE , CM.RPRSV_FLNM , CM.RPRSV_TELNO
, CM.CRNO , CM.CORP_SE_CD , CM.CORP_DTL_SE_CD , CM.CORP_FNDN_DAY , CM.INVST_AMT , CM.FRMER_INVST_AMT , CM.PRDCR_GRP_INVST_AMT , CM.LOCGOV_INVST_AMT , CM.ETC_INVST_AMT , CM.FXNO
, CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID
, CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_EV_APO CM WHERE 1=1
AND CM.UO_BRNO = #{uoBrno}
AND CM.BRNO = #{brno}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.selectInvShipOgnReqMngList */ CM.APO_CD , CM.APO_SE , CM.WRT_YN , CM.CORP_NM , CM.BRNO , CM.INVST_NOPE , CM.RPRSV_FLNM , CM.INVST_EXPND_FRMER_NOPE , CM.RPRSV_TELNO
, CM.CRNO , CM.CORP_SE_CD , CM.CORP_DTL_SE_CD , CM.CORP_FNDN_DAY , CM.INVST_AMT , CM.FRMER_INVST_AMT , CM.PRDCR_GRP_INVST_AMT , CM.LOCGOV_INVST_AMT , CM.ETC_INVST_AMT , ( ROUND ( FRMER_INVST_AMT / DECODE(INVST_AMT, 0, NULL, INVST_AMT) * 100, 2) ) AS FRMER_INVST_AMT_RT , CM.FXNO
, (SELECT 'Y' FROM TB_EV_APLY_MNG WHERE BRNO = CM.BRNO AND YR = EAM.YR ) AS APLY_CHK
, (CASE WHEN CM.APO_SE = '1' THEN '통합조직 가입' WHEN CM.APO_SE = '2' THEN '출자출하조직 가입' ELSE '없음' END ) AS USER_TYPE
, EAM.YR , EAM.APLY_DAY , (CASE WHEN CM.APO_SE = '1' THEN EAM.CORP_DDLN_SE_CD WHEN CM.APO_SE = '2' THEN (SELECT CORP_DDLN_SE_CD FROM TB_EV_APLY_MNG WHERE YR = EAM.YR AND BRNO = EAM.UO_BRNO) END) AS CORP_DDLN_SE_CD , EAM.ISO_FUND_APLY_AMT , EAM.APLY_TRGT_SE , EAM.UO_CD , EAM.UO_BRNO
, UO.UO_BRNO AS UO_BRNO_DEL
, CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID
, CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_EV_APO_UO_CD UO, TB_EV_APO CM, (SELECT * FROM TB_EV_APLY_MNG WHERE 1=1
AND YR = #{yr}
) EAM WHERE 1=1 AND UO.ISO_BRNO = CM.BRNO(+) AND CM.BRNO = EAM.BRNO(+) AND CM.BRNO IS NOT NULL
AND UO.UO_BRNO = #{uoBrno}
AND UO.ISO_BRNO = #{isoBrno}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	MERGE INTO TB_EV_APLY_MNG CLT  /* com.at.apcss.pd.aom.mapper.InvShipOgnReqMngMapper.insertInvShipOgnReqMng */
		USING DUAL
			ON (
				CLT.APO_CD  = #{apoCd}
				AND CLT.YR  = #{yr}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	          ISO_FUND_APLY_AMT			= #{isoFundAplyAmt}
		    	        , APLY_TRGT_SE				= #{aplyTrgtSe}
		    	        , UO_BRNO					= #{uoBrno}
		    	        , UO_CD						= #{uoCd}
		    	        , APO_SE					= #{apoSe}

		    	        --, APLY_DAY	= SYSDATE


					 WHERE 1=1
					 AND CLT.APO_CD	= #{apoCd}
					 AND CLT.YR  	= #{yr}
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , CORP_NM
		    		  , YR
		    		  , APLY_DAY
		    		  , UO_BRNO
		    		  , UO_CD
		    		  , ISO_FUND_APLY_AMT
		    		  , APLY_TRGT_SE
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , #{apoSe}
		   			  , #{brno}
		   			  , #{crno}
		   			  , #{corpNm}
		   			  , TO_CHAR(SYSDATE,'YYYY')
		   			  , SYSDATE
		   			  , #{uoBrno}
		   			  , #{uoCd}
		   			  , #{isoFundAplyAmt}
		   			  , #{aplyTrgtSe}
		   			)

mapper.update:

    	UPDATE /* com.at.apcss.pd.aom.mapper.InvShipOgnReqMngMapper.upateInvShipOgnReqMng */
    		TB_EV_APLY_MNG
		   SET
		 	  ISO_FUND_APLY_AMT			= #{isoFundAplyAmt}
	    	, APLY_TRGT_SE				= #{aplyTrgtSe}


		 WHERE 1=1
		   AND CLT.APO_CD		= #{apoCd}

mapper.delete:

DELETE FROM TB_EV_APO_UO_CD	/* com.at.apcss.pd.aom.mapper.InvShipOgnReqMngMapper.deleteInvShipOgnReqMng */
 WHERE ISO_BRNO = #{brno}
   AND UO_BRNO = #{uoBrnoDel}
