SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT * FROM (SELECT /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.selectPrdcrCrclOgnReqMngList */ CM.APO_CD , CM.APO_SE , CM.WRT_YN , CM.CTPV , CM.SGG , CM.CORP_NM , CM.BRNO , CM.CRNO
, CM.PIC_FLNM , CM.ZIP , CM.LOTNO_ADDR , CM.LOTNO_DTL_ADDR , CM.ROAD_NM_ADDR , CM.ROAD_NM_DTL_ADDR , CM.CORP_SE_CD , CM.CORP_DTL_SE_CD , CM.CORP_FNDN_DAY , CM.INVST_NOPE , CM.INVST_EXPND_FRMER_NOPE , CM.INVST_AMT , CM.FRMER_INVST_AMT , CM.PRDCR_GRP_INVST_AMT , CM.LOCGOV_INVST_AMT , CM.ETC_INVST_AMT , CM.RGLLBR_NOPE , CM.DW_NOPE , CM.DLBRR_NOPE , CM.ISO_HLD_YN , CM.UNTY_YN , CM.UO_BRNO , CM.UO_NM , CM.UNTY_YR
, (CASE WHEN CM.APO_SE = '1' THEN CM.APRV WHEN CM.APO_SE = '2' THEN (SELECT (SELECT APRV FROM TB_EV_APO WHERE BRNO = AM.UO_BRNO) AS APRV FROM TB_EV_APLY_MNG AM WHERE 1=1 AND BRNO = CM.BRNO AND APO_SE = '2' AND YR = #{yr} ) ELSE '' END ) AS APRV
, CM.STBLT_YN
,(SELECT LISTAGG(ITEM_NM, ' , ') WITHIN GROUP (ORDER BY ITEM_NM) AS STBLT_YN_NM FROM (SELECT (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = A.ITEM_CD) AS ITEM_NM FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT A WHERE 1=1 AND STBLT_YN = 'Y' AND APO_SE = CM.APO_SE AND TYPE_SE_NO = '8'
AND UO_BRNO = #{brno}
AND YR = EAM.YR AND BRNO = CM.BRNO ) ) AS STBLT_YN_NM
, CM.MNGMST_YN
, NVL((SELECT COUNT(*) FROM TB_EV_FRMHS_APO WHERE 1=1 AND YR = EAM.YR AND BRNO = CM.BRNO ),0) AS FRMHS_CNT
, EAM.YR , EAM.APLY_DAY , EAM.CORP_DDLN_SE_CD ,(CASE WHEN TRIM(RAW_MTR_ENSR_SIGUN_CNT) IS NOT NULL THEN '1' WHEN TRIM(RAW_MTR_ENSR_CTPV_CNT) IS NOT NULL THEN '2' END ) AS RAW_MTR_ENSR
, EAM.RAW_MTR_ENSR_SIGUN_CNT , EAM.RAW_MTR_ENSR_CTPV_CNT , EAM.CTPV_NM , EAM.SIGUN_NM , EAM.PRUO_FUND_APLY_AMT , (SELECT SUM(ISO_FUND_APLY_AMT) FROM (SELECT ISO_FUND_APLY_AMT FROM TB_EV_APLY_MNG WHERE BRNO IN (SELECT ISO_BRNO FROM TB_EV_APO_UO_CD WHERE UO_BRNO = CM.BRNO) ) ) AS ISO_FUND_APLY_AMT , EAM.APLY_TRGT_SE
, CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID
, CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM (SELECT * FROM TB_EV_APO WHERE 1=1
AND BRNO = #{brno}
OR BRNO IN (SELECT ISO_BRNO FROM TB_EV_APO_UO_CD WHERE UO_BRNO = #{brno})
AND BRNO in (SELECT ISO_BRNO FROM TB_EV_APO_UO_CD WHERE UO_BRNO = #{uoBrno})
) CM , (SELECT * FROM TB_EV_APLY_MNG WHERE 1=1
AND YR = #{yr}
) EAM WHERE 1=1 AND CM.BRNO = EAM.BRNO(+) AND CM.APO_SE IS NOT NULL AND CM.APO_CD IS NOT NULL
AND CM.APO_SE = #{apoSe}
AND CM.CMPTN_INST = #{cmptnInst}
AND CM.CTPV = #{ctpv}
AND CM.CORP_SE_CD = #{corpSeCd}
AND CM.CORP_DTL_SE_CD = #{corpDtlSeCd}
AND CM.CORP_NM LIKE '%' || #{corpNm} || '%'
AND EAM.CORP_DDLN_SE_CD = #{corpDdlnSeCd}
AND EAM.YR = #{yr}
) WHERE 1=1
AND APRV = #{aprv}
AND FRMHS_CNT > 0
AND FRMHS_CNT = 0
