SELECT /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.selectGpcList */ CM.APO_CD , CM.APO_SE , CM.CORP_NM , CM.BRNO , CM.CRNO
, CM.YR , CM.STTG_UPBR_ITEM_SE , CM.CTGRY_CD , CM.CTGRY_NM , CM.ITEM_CD , CM.ITEM_NM , CM.RMRK
, CM.DEL_YN
, CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID
, CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_EV_APO_YR_GPC_CD CM WHERE 1=1
AND CM.APO_CD = #{apoCd}
