mapper.select:
SELECT /* com.at.apcss.pd.bsm.mapper.PrdcrCrclOgnUsrMngMapper.selectPrdcrCrclOgnUsrMng */ CM.USER_ID , CM.USER_NM , CM.USER_TYPE , CM.USER_STTS , CM.USER_APRV_YMD , CM.TELNO , CM.BRNO , CM.MBL_TELNO , CM.CO_NM , CM.USER_APRV_YMD , CM.CMPTNC_INST_APRV_SE , CM.CMPTNC_INST_APRV_YMD , CM.CMPTNC_INST , CM.UO_APRV_STTS , CM.DEL_YN AS DEL_YN , CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_ID_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_ID_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_COM_USER CM WHERE 1=1 AND DEL_YN = 'N'
AND CM.USER_ID = #{userId}
ORDER BY CM.USER_ID DESC

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.pd.bsm.mapper.PrdcrCrclOgnUsrMngMapper.selectPrdcrCrclOgnUsrMng */ CM.USER_ID , CM.USER_NM , CM.USER_TYPE , CM.USER_STTS , CM.USER_APRV_YMD , CM.TELNO , CM.BRNO , CM.MBL_TELNO , CM.CO_NM , CM.USER_APRV_YMD , CM.CMPTNC_INST_APRV_SE , CM.CMPTNC_INST_APRV_YMD , CM.CMPTNC_INST , CM.UO_APRV_STTS , CM.DEL_YN AS DEL_YN , CM.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , TO_CHAR(CM.SYS_FRST_INPT_DT,'YYYY-MM-DD') AS SYS_FRST_INPT_DT_YMD , CM.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_FRST_INPT_USER_ID) AS SYS_FRST_INPT_USER_ID_NM , CM.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_ID_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_COM_USER CM WHERE 1=1 AND DEL_YN = 'N'
AND CM.USER_ID = #{userId}
AND CM.USER_NM LIKE '%' || #{userNm} || '%'
AND CM.CO_NM LIKE '%' || #{coNm} || '%'
AND CM.USER_TYPE = #{userType}
AND CM.USER_STTS = #{userStts}
AND CM.BRNO = #{brno}
AND CM.CMPTNC_INST_APRV_SE = #{cmptncInstAprvSe}
AND CM.CMPTNC_INST = #{cmptncInst}
ORDER BY CM.USER_ID DESC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

