		USING DUAL
			ON (
					CLT.BRNO   			= #{brno}
				AND CLT.UO_BRNO	 		= #{uoBrno}
				AND CLT.CTGRY_CD 		= #{ctgryCd}
				AND CLT.ITEM_CD	 		= #{itemCd}
				AND CLT.PRCHS_SLS_SE	= '1'
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        PRCHS_TRST_VLM			= #{prchsTrstVlm}
		    	        , PRCHS_TRST_AMT		= #{prchsTrstAmt}
		    	        , PRCHS_EMSPAP_VLM		= #{prchsEmspapVlm}
		    	        , PRCHS_EMSPAP_AMT		= #{prchsEmspapAmt}
		    	        , ETC_VLM				= #{etcVlm}
		    	        , ETC_AMT				= #{etcAmt}

		    	        , RMRK					= #{rmrk}

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					 	AND CLT.BRNO   			= #{brno}
						AND CLT.UO_BRNO	 		= #{uoBrno}
						AND CLT.CTGRY_CD 		= #{ctgryCd}
						AND CLT.ITEM_CD	 		= #{itemCd}
						AND CLT.PRCHS_SLS_SE	= '2'
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , UO_BRNO
		    		  , CRNO
		    		  , YR

		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , STTG_UPBR_ITEM_SE
		    		  , PRCHS_SLS_SE

		    		  , PRCHS_TRST_VLM
		    		  , PRCHS_TRST_AMT
		    		  , PRCHS_EMSPAP_VLM
		    		  , PRCHS_EMSPAP_AMT
		    		  , ETC_VLM
		    	      , ETC_AMT

		    		  , RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , (case when #{apoSe} is not null then #{apoSe}
		   			  			ELSE (SELECT OGNZ_SE_CD FROM TB_APC_OGNZ_INFO WHERE OGNZ_CD = #{apoCd})
		   			  			END)
		   			  , #{brno}
		   			  , #{uoBrno}
		   			  , (case when #{crno} is not null then #{crno}
		   			  			ELSE (SELECT CRNO FROM TB_EV_APO WHERE BRNO = #{brno})
		   			  			END)
		   			  , TO_CHAR(SYSDATE,'YYYY')

		   			  , (case when #{ctgryCd} is not null then #{ctgryCd}
		   			  			ELSE (SELECT CTGRY_CD FROM TB_EV_APO_YR_GPC_CD WHERE ITEM_CD = #{itemCd} AND BRNO = #{uoBrno})
		   			  			END)
		   			  , #{itemCd}
		   			  , #{sttgUpbrItemSe}
		   			  , #{prchsSlsSe}

		   			  , #{prchsTrstVlm}
		   			  , #{prchsTrstAmt}
		   			  , #{prchsEmspapVlm}
		   			  , #{prchsEmspapAmt}
		   			  , #{etcVlm}
		   			  , #{etcAmt}

		   			  , #{rmrk}

		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)


