mapper.insert:

    	MERGE INTO TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT CLT  /* com.at.apcss.pd.isom.mapper.InvShipOgnSpeczItmPurSalMngMapper.insertInvShipOgnSpeczItmPurSalMng */
		USING DUAL
			ON (
					CLT.APO_CD   		= #{apoCd}
				AND CLT.APO_SE	 		= #{apoSe}
				AND CLT.BRNO 			= #{brno}
				AND CLT.YR	 			= #{yr}
				AND CLT.CTGRY_CD	 	= #{ctgryCd}
				AND CLT.ITEM_CD 		= #{itemCd}
				AND CLT.PRDCR_OGNZ_SN 	= #{prdcrOgnzSn}
				AND CLT.CLTVTN_LAND_SN 	= #{cltvtnLandSn}
				AND CLT.TYPE_SE_NO		= #{typeSeNo}
				AND CLT.UO_BRNO 		= #{uoBrno}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        SLS_CNSGN_PRCHS_AMT		= #{slsCnsgnPrchsAmt}
		    	        , UO_SPMT_AMT			= #{uoSpmtAmt}
		    	        , UO_OTHER_SPMT_AMT		= #{uoOtherSpmtAmt}
		    	        , TRMT_TYPE				= #{trmtType}

		    	        , RMRK					= #{rmrk}

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					   AND CLT.APO_CD   		= #{apoCd}
					   AND CLT.APO_SE	 		= #{apoSe}
					   AND CLT.BRNO 			= #{brno}
					   AND CLT.YR	 			= #{yr}
					   AND CLT.CTGRY_CD	 		= #{ctgryCd}
					   AND CLT.ITEM_CD 			= #{itemCd}
					   AND CLT.PRDCR_OGNZ_SN 	= #{prdcrOgnzSn}
					   AND CLT.CLTVTN_LAND_SN 	= #{cltvtnLandSn}
					   AND CLT.TYPE_SE_NO		= #{typeSeNo}
					   AND CLT.UO_BRNO 			= #{uoBrno}
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR
		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , ISO_BRNO
		    		  , PRDCR_OGNZ_SN
		    		  , PRDCR_OGNZ_CD
		    		  , CLTVTN_LAND_SN
		    		  , TYPE_SE_NO
		    		  , UO_BRNO
		    		  , TRMT_TYPE

		    		  , SLS_CNSGN_PRCHS_AMT
		    		  , UO_SPMT_AMT
		    		  , UO_OTHER_SPMT_AMT

		    		  , RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		    		  , #{apoSe}
		    		  , #{brno}
		    		  , #{crno}
		    		  , TO_CHAR(SYSDATE,'YYYY')
		    		  , #{ctgryCd}
		    		  , #{itemCd}
		    		  , #{isoBrno}
		    		  , #{prdcrOgnzSn}
		    		  , #{prdcrOgnzCd}
		    		  , #{cltvtnLandSn}
		    		  , #{typeSeNo}
		    		  , #{uoBrno}
		    		  , #{trmtType}

		   			  , #{slsCnsgnPrchsAmt}
		   			  , #{uoSpmtAmt}
		   			  , #{uoOtherSpmtAmt}

		   			  , #{rmrk}

		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)

mapper.select:
SELECT /* com.at.apcss.pd.isom.mapper.InvShipOgnSpeczItmPurSalMngMapper.selectItemStbltYn */ TOTAL.ITEM_CD ,TOTAL.ITEM_NM ,TOTAL.STTG_UPBR_ITEM_SE ,TOTAL.STTG_UPBR_ITEM_NM ,TOTAL.CTGRY_CD ,TOTAL.CTGRY_NM ,TOTAL.BRNO ,TOTAL.UO_BRNO ,TOTAL.YR ,TOTAL.APRV ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'APRV_UPBR_SE_CD' AND CD_VL = TOTAL.APRV) AS APRV_NM ,TOTAL.CORP_NM ,TOTAL.UO_SPMT_AMT ,TOTAL.UO_SPMT_AMT_OTHER ,TOTAL.UO_OTHER_SPMT_AMT ,TOTAL.UO_OTHER_SPMT_AMT_OTHER ,TOTAL.UO_SPMT_AMT_TOT ,TOTAL.UO_SPMT_AMT_RT ,TOTAL.UO_SPMT_AMT_TOT_RT
,TOTAL.CHK_A_A ,TOTAL.CHK_A_B ,TOTAL.CHK_A_C
,TOTAL.CHK_B_A ,TOTAL.CHK_B_B ,TOTAL.CHK_B_C , (CASE WHEN TOTAL.APRV = '1' AND STTG_UPBR_ITEM_SE = '1' THEN (CASE WHEN TOTAL.CHK_A_A = 'Y' AND TOTAL.CHK_A_B = 'Y' AND TOTAL.CHK_A_C = 'Y' THEN 'Y' ELSE 'N' END) WHEN TOTAL.APRV = '1' AND STTG_UPBR_ITEM_SE = '2' THEN 'Y' WHEN TOTAL.APRV = '2' THEN (CASE WHEN TOTAL.CHK_B_A = 'Y' AND TOTAL.CHK_B_B = 'Y' AND TOTAL.CHK_B_C = 'Y' THEN 'Y' ELSE 'N' END) ELSE 'N' END ) AS STBLT_YN
FROM (SELECT TOT.ITEM_CD ,TOT.ITEM_NM ,TOT.STTG_UPBR_ITEM_SE ,TOT.STTG_UPBR_ITEM_NM ,TOT.CTGRY_CD ,TOT.CTGRY_NM ,TOT.BRNO ,TOT.UO_BRNO ,TOT.YR ,TOT.APRV ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'APRV_UPBR_SE_CD' AND CD_VL = TOT.APRV) AS APRV_NM ,TOT.CORP_NM ,TOT.UO_SPMT_AMT ,TOT.UO_SPMT_AMT_OTHER ,TOT.UO_OTHER_SPMT_AMT ,TOT.UO_OTHER_SPMT_AMT_OTHER ,TOT.UO_SPMT_AMT_TOT ,TOT.UO_SPMT_AMT_RT ,TOT.UO_SPMT_AMT_TOT_RT
,(CASE WHEN TOT.UO_SPMT_AMT >= 500000 THEN 'Y' ELSE 'N' END) AS CHK_A_A ,(CASE WHEN TOT.UO_SPMT_AMT_RT >= 60 THEN 'Y' ELSE 'N' END) AS CHK_A_B ,(CASE WHEN TOT.UO_SPMT_AMT_RT >= 60 THEN 'Y' ELSE 'N' END) AS CHK_A_C
,(CASE WHEN TOT.UO_SPMT_AMT >= 300000 THEN 'Y' ELSE 'N' END) AS CHK_B_A ,(CASE WHEN TOT.UO_SPMT_AMT_RT >= 100 THEN 'Y' ELSE 'N' END) AS CHK_B_B ,(CASE WHEN TOT.UO_SPMT_AMT_RT >= 20 THEN 'Y' ELSE 'N' END) AS CHK_B_C FROM (SELECT ITEM.ITEM_CD , ITEM.ITEM_NM , ITEM.STTG_UPBR_ITEM_SE , ITEM.STTG_UPBR_ITEM_NM , ITEM.CTGRY_CD , ITEM.CTGRY_NM , ITEM.BRNO , ITEM.UO_BRNO , ITEM.YR , (SELECT APRV FROM TB_EV_APO WHERE BRNO = ITEM.UO_BRNO) AS APRV , (SELECT CORP_NM FROM TB_EV_APO WHERE BRNO = ITEM.BRNO) AS CORP_NM
, TOTA.UO_SPMT_AMT AS UO_SPMT_AMT , TOTA.UO_OTHER_SPMT_AMT AS UO_SPMT_AMT_OTHER
, TOTB.UO_SPMT_AMT AS UO_OTHER_SPMT_AMT , TOTB.UO_OTHER_SPMT_AMT AS UO_OTHER_SPMT_AMT_OTHER
, (TOTA.UO_SPMT_AMT + TOTA.UO_OTHER_SPMT_AMT + TOTB.UO_SPMT_AMT + TOTB.UO_OTHER_SPMT_AMT) AS UO_SPMT_AMT_TOT
, ROUND(NVL( TOTA.UO_SPMT_AMT / NULLIF( TOTA.UO_SPMT_AMT + TOTB.UO_SPMT_AMT , 0 ) , 0 ) * 100 , 2 ) AS UO_SPMT_AMT_RT , ROUND(NVL( (TOTA.UO_SPMT_AMT + TOTA.UO_OTHER_SPMT_AMT) / NULLIF( TOTA.UO_SPMT_AMT + TOTA.UO_OTHER_SPMT_AMT + TOTB.UO_SPMT_AMT + TOTB.UO_OTHER_SPMT_AMT, 0 ) , 0 ) * 100 , 2) AS UO_SPMT_AMT_TOT_RT FROM (SELECT ITEM_CD , YR , STTG_UPBR_ITEM_SE , CTGRY_CD , TO_CHAR(BRNO) AS UO_BRNO , #{brno} AS BRNO ,(SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = AYGC.ITEM_CD) AS ITEM_NM ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'CTGRY_CD' AND CD_VL = AYGC.CTGRY_CD) AS CTGRY_NM ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'STTG_UPBR_ITEM_SE' AND CD_VL = AYGC.STTG_UPBR_ITEM_SE) AS STTG_UPBR_ITEM_NM FROM TB_EV_APO_YR_GPC_CD AYGC WHERE 1=1
AND BRNO = #{uoBrno}
AND YR = #{yr}
) ITEM , (SELECT BRNO , ITEM_CD , SUM( NVL( UO_SPMT_AMT , 0 ) ) AS UO_SPMT_AMT , SUM( NVL( UO_OTHER_SPMT_AMT , 0 ) ) AS UO_OTHER_SPMT_AMT FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '2' AND TYPE_SE_NO IN ('5')
AND UO_BRNO = #{uoBrno}
AND BRNO = #{brno}
AND YR = #{yr}
GROUP BY BRNO , ITEM_CD ) TOTA , (SELECT BRNO , ITEM_CD , SUM( NVL( UO_SPMT_AMT , 0 ) ) AS UO_SPMT_AMT , SUM( NVL( UO_OTHER_SPMT_AMT , 0 ) ) AS UO_OTHER_SPMT_AMT FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '2' AND TYPE_SE_NO IN ('7')
AND UO_BRNO = #{uoBrno}
AND BRNO = #{brno}
AND YR = #{yr}
GROUP BY BRNO , ITEM_CD ) TOTB WHERE 1=1 AND ITEM.ITEM_CD = TOTA.ITEM_CD(+) AND ITEM.ITEM_CD = TOTB.ITEM_CD(+) )TOT )TOTAL

mapper.update:
    	UPDATE /* com.at.apcss.pd.isom.mapper.InvShipOgnSpeczItmPurSalMngMapper.updateItemStbltYn */
    		TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT
		   SET
		 	  STBLT_YN	= #{stbltYn}
		 WHERE 1=1
		   AND APO_SE 	= '2'
		   AND UO_BRNO 	= #{uoBrno}
		   AND BRNO 	= #{brno}
		   AND YR 		= #{yr}
		   AND ITEM_CD 	= #{itemCd}
