SELECT * /* com.at.apcss.pd.isom.mapper.InvShipOgnSpeczItmPurSalMngMapper.selectInvShipOgnSpeczItmPurSalMng */ FROM ( SELECT ST.APO_CD , ST.APO_SE ,'생산자조직(전속출하)' AS SE_NM , ST.BRNO , ST.YR , ST.CTGRY_CD , ST.ITEM_CD , ST.STTG_UPBR_ITEM_SE , (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = ST.ITEM_CD) AS ITEM_NM , 0 AS ISO_BRNO , ST.PRDCR_OGNZ_SN , ST.PRDCR_OGNZ_CD , ST.PRDCR_OGNZ_NM , (SELECT SUM(SPMT_PRC) FROM TB_EV_FRMHS_PRDCTN_EC_SPMT_STTN WHERE PRDCR_OGNZ_SN = ST.PRDCR_OGNZ_SN) AS SLS_CNSGN_PRCHS_AMT , TOT.UO_SPMT_AMT , TOT.UO_OTHER_SPMT_AMT , TOT.RMRK FROM TB_EV_FRMHS_APO ST LEFT JOIN TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT TOT ON ST.APO_CD = TOT.APO_CD AND ST.APO_SE = TOT.APO_SE AND ST.YR = TOT.YR AND ST.CTGRY_CD = TOT.CTGRY_CD AND ST.ITEM_CD = TOT.ITEM_CD AND ST.PRDCR_OGNZ_SN = TOT.PRDCR_OGNZ_SN AND ST.YR = #{yr} WHERE 1=1
AND ST.BRNO = #{brno}
UNION ALL
SELECT ITEM.APO_CD , ITEM.APO_SE ,'생산자조직 외' AS SE_NM , ITEM.BRNO , ITEM.YR , ITEM.CTGRY_CD , ITEM.ITEM_CD , ITEM.STTG_UPBR_ITEM_SE , (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = ITEM.ITEM_CD) AS ITEM_NM , 0 AS ISO_BRNO , 0 AS PRDCR_OGNZ_SN , '99' AS PRDCR_OGNZ_CD , '농가 법인 등' AS PRDCR_OGNZ_NM , (CASE WHEN TOT.SLS_CNSGN_PRCHS_AMT IS NOT NULL THEN TOT.SLS_CNSGN_PRCHS_AMT ELSE '' END) AS SLS_CNSGN_PRCHS_AMT , TOT.UO_SPMT_AMT , TOT.UO_OTHER_SPMT_AMT , TOT.RMRK FROM (SELECT ITEM_CD , APO_CD , APO_SE , YR , BRNO , STTG_UPBR_ITEM_SE , CTGRY_CD FROM TB_EV_FRMHS_APO WHERE 1=1 AND YR = #{yr} GROUP BY ITEM_CD , APO_CD , APO_SE , YR , BRNO , STTG_UPBR_ITEM_SE , CTGRY_CD ) ITEM LEFT JOIN TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT TOT ON ITEM.ITEM_CD = TOT.ITEM_CD AND ITEM.APO_CD = TOT.APO_CD AND TOT.PRDCR_OGNZ_CD = '99' AND TOT.ISO_BRNO = '0' AND TOT.PRDCR_OGNZ_SN = '0' WHERE 1=1
AND ITEM.BRNO = #{brno}
) ORDER BY ITEM_CD , PRDCR_OGNZ_SN DESC

SELECT * /* com.at.apcss.pd.isom.mapper.InvShipOgnSpeczItmPurSalMngMapper.selectInvShipOgnSpeczItmPurSalMngList */ FROM ( SELECT ST.APO_CD , ST.APO_SE ,'생산자조직(전속출하)' AS SE_NM , ST.BRNO , ST.YR , ST.CTGRY_CD , ST.ITEM_CD , ST.STTG_UPBR_ITEM_SE , (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = ST.ITEM_CD) AS ITEM_NM , 0 AS ISO_BRNO , ST.PRDCR_OGNZ_SN , ST.PRDCR_OGNZ_CD , ST.PRDCR_OGNZ_NM , (SELECT SUM(SPMT_PRC) FROM TB_EV_FRMHS_PRDCTN_EC_SPMT_STTN WHERE PRDCR_OGNZ_SN = ST.PRDCR_OGNZ_SN) AS SLS_CNSGN_PRCHS_AMT , TOT.UO_SPMT_AMT , TOT.UO_OTHER_SPMT_AMT , TOT.RMRK FROM TB_EV_FRMHS_APO ST LEFT JOIN TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT TOT ON ST.APO_CD = TOT.APO_CD AND ST.APO_SE = TOT.APO_SE AND ST.YR = TOT.YR AND ST.CTGRY_CD = TOT.CTGRY_CD AND ST.ITEM_CD = TOT.ITEM_CD AND ST.PRDCR_OGNZ_SN = TOT.PRDCR_OGNZ_SN AND ST.YR = #{yr} WHERE 1=1
AND ST.BRNO = #{brno}
UNION ALL
SELECT ITEM.APO_CD , ITEM.APO_SE ,'생산자조직 외' AS SE_NM , ITEM.BRNO , ITEM.YR , ITEM.CTGRY_CD , ITEM.ITEM_CD , ITEM.STTG_UPBR_ITEM_SE , (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = ITEM.ITEM_CD) AS ITEM_NM , 0 AS ISO_BRNO , 0 AS PRDCR_OGNZ_SN , '99' AS PRDCR_OGNZ_CD , '농가 법인 등' AS PRDCR_OGNZ_NM , (CASE WHEN TOT.SLS_CNSGN_PRCHS_AMT IS NOT NULL THEN TOT.SLS_CNSGN_PRCHS_AMT ELSE '' END) AS SLS_CNSGN_PRCHS_AMT , TOT.UO_SPMT_AMT , TOT.UO_OTHER_SPMT_AMT , TOT.RMRK FROM (SELECT ITEM_CD , APO_CD , APO_SE , YR , BRNO , STTG_UPBR_ITEM_SE , CTGRY_CD FROM TB_EV_FRMHS_APO WHERE 1=1 AND YR = #{yr} GROUP BY ITEM_CD , APO_CD , APO_SE , YR , BRNO , STTG_UPBR_ITEM_SE , CTGRY_CD ) ITEM LEFT JOIN TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT TOT ON ITEM.ITEM_CD = TOT.ITEM_CD AND ITEM.APO_CD = TOT.APO_CD AND TOT.PRDCR_OGNZ_CD = '99' AND TOT.ISO_BRNO = '0' AND TOT.PRDCR_OGNZ_SN = '0' WHERE 1=1
AND ITEM.BRNO = #{brno}
