mapper.insert:

    	MERGE INTO TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT CLT  /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnPurSalMngMapper.insertPrdcrCrclOgnPurSalMng */
		USING DUAL
			ON (
					CLT.APO_CD   		= #{apoCd}
				AND CLT.APO_SE	 		= #{apoSe}
				AND CLT.BRNO 			= #{brno}
				AND CLT.YR	 			= #{yr}
				AND CLT.CTGRY_CD	 	= #{ctgryCd}
				AND CLT.ITEM_CD 		= #{itemCd}
				AND CLT.PRDCR_OGNZ_SN 	= #{prdcrOgnzSn}
				AND CLT.ISO_BRNO 		= #{isoBrno}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        SLS_CNSGN_PRCHS_AMT		= #{slsCnsgnPrchsAmt}
		    	        , UO_SPMT_AMT			= #{uoSpmtAmt}
		    	        , UO_OTHER_SPMT_AMT		= #{uoOtherSpmtAmt}

		    	        , RMRK					= #{rmrk}

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					   AND CLT.APO_CD   		= #{apoCd}
					   AND CLT.APO_SE	 		= #{apoSe}
					   AND CLT.BRNO 			= #{brno}
					   AND CLT.YR	 			= #{yr}
					   AND CLT.CTGRY_CD	 		= #{ctgryCd}
					   AND CLT.ITEM_CD 			= #{itemCd}
					   AND CLT.PRDCR_OGNZ_SN 	= #{prdcrOgnzSn}
					   AND CLT.ISO_BRNO 		= '0'
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR
		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , ISO_BRNO
		    		  , PRDCR_OGNZ_SN
		    		  , PRDCR_OGNZ_CD

		    		  , SLS_CNSGN_PRCHS_AMT
		    		  , UO_SPMT_AMT
		    		  , UO_OTHER_SPMT_AMT

		    		  , RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		    		  , #{apoSe}
		    		  , #{brno}
		    		  , #{crno}
		    		  , TO_CHAR(SYSDATE,'YYYY')
		    		  , #{ctgryCd}
		    		  , #{itemCd}
		    		  , '0'
		    		  , #{prdcrOgnzSn}
		    		  , #{prdcrOgnzCd}

		   			  , #{slsCnsgnPrchsAmt}
		   			  , #{uoSpmtAmt}
		   			  , #{uoOtherSpmtAmt}

		   			  , #{rmrk}

		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)

