mapper.select:
SELECT /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnOnlnWhlslMrktMapper.selectPrdcrCrclOgnOnlnWhlslMrktUoList */ EAM.YR ,EAM.BRNO ,EA.CRNO ,EA.CORP_NM ,EA.APO_CD ,EA.SLCTN_YR ,(SELECT LISTAGG(ITEM_NM, ' , ') WITHIN GROUP (ORDER BY ITEM_NM) FROM (SELECT (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = YGC.ITEM_CD) AS ITEM_NM FROM TB_EV_APO_YR_GPC_CD YGC WHERE 1=1 AND STTG_UPBR_ITEM_SE IN ('1','2') AND APO_SE = '1' AND YR = EAM.YR AND BRNO = EAM.BRNO ) )AS ITEM_NM_LIST ,(SELECT 'Y' FROM TB_EV_ONLN_WHLSL_MRKT WHERE YR = EAM.YR AND BRNO = EAM.BRNO)AS CHK FROM TB_EV_APLY_MNG EAM ,TB_EV_APO EA WHERE 1=1 AND EAM.BRNO = EA.BRNO(+) AND EAM.APO_SE = '1' AND EAM.YR = #{yr}
AND EAM.BRNO = #{brno}
AND EAM.BRNO IN (SELECT BRNO FROM TB_EV_APO WHERE CORP_NM LIKE '%' || #{corpNm} || '%')

mapper.insert:
MERGE INTO TB_EV_ONLN_WHLSL_MRKT CLT /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnOnlnWhlslMrktMapper.insertPrdcrCrclOgnOnlnWhlslMrkt */
USING DUAL
	ON (
			CLT.YR			= #{yr}
			AND CLT.BRNO	= #{brno}
		)
WHEN MATCHED THEN
		UPDATE
			   SET
				  TRGT_TRMT_AMT			= #{trgtTrmtAmt}
				, CONSIGN_TRGT_AMT		= #{consignTrgtAmt}
				, CONSIGN_CRTD_TRGT_AMT	= #{consignCrtdTrgtAmt}
				, TOT_TRGT_TRMT_AMT		= #{totTrgtTrmtAmt}
				, UO_TOT_TRGT_TRMT_AMT	= #{uoTotTrgtTrmtAmt}
				, TRGT_TRMT_RT			= #{trgtTrmtRt}
				, CRTR_AMT				= #{crtrAmt}

				, SYS_LAST_CHG_DT		= SYSDATE
				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
	 INSERT
			(
				YR
			  , BRNO

			  , TRGT_TRMT_AMT
			  , CONSIGN_TRGT_AMT
			  , CONSIGN_CRTD_TRGT_AMT
			  , TOT_TRGT_TRMT_AMT
			  , UO_TOT_TRGT_TRMT_AMT
			  , TRGT_TRMT_RT
			  , CRTR_AMT

			  , SYS_FRST_INPT_DT
			  , SYS_FRST_INPT_USER_ID
			  , SYS_FRST_INPT_PRGRM_ID
			  , SYS_LAST_CHG_DT
			  , SYS_LAST_CHG_USER_ID
			  , SYS_LAST_CHG_PRGRM_ID
			)
		VALUES
			(
			  #{yr}
			  , #{brno}

			  , #{trgtTrmtAmt}
			  , #{consignTrgtAmt}
			  , #{consignCrtdTrgtAmt}
			  , #{totTrgtTrmtAmt}
			  , #{uoTotTrgtTrmtAmt}
			  , #{trgtTrmtRt}
			  , #{crtrAmt}

			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)

MERGE INTO TB_EV_ONLN_WHLSL_MRKT_DTL WMD /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnOnlnWhlslMrktMapper.insertOnlnDtl */
USING DUAL
	ON (
			WMD.YR			= #{yr}
			AND WMD.BRNO	= #{brno}
			AND WMD.UO_BRNO	= #{uoBrno}
			AND WMD.ITEM_CD	= #{itemCd}
		)
WHEN MATCHED THEN
		UPDATE
			   SET
				TRMT_AMT			= #{trmtAmt}
				, CONSIGN_TRMT_AMT	= #{consignTrmtAmt}
				, CTGRY_CD			= #{ctgryCd}
				, CLSF_CD			= #{clsfCd}

				, SYS_LAST_CHG_DT		= SYSDATE
				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
	 INSERT
			(
				YR
			  , BRNO
			  , UO_BRNO

			  , ITEM_CD
			  , CTGRY_CD
			  , CLSF_CD

			  , TRMT_AMT
			  , CONSIGN_TRMT_AMT

			  , SYS_FRST_INPT_DT
			  , SYS_FRST_INPT_USER_ID
			  , SYS_FRST_INPT_PRGRM_ID
			  , SYS_LAST_CHG_DT
			  , SYS_LAST_CHG_USER_ID
			  , SYS_LAST_CHG_PRGRM_ID
			)
		VALUES
			(
			  #{yr}
			  , #{brno}
			  , #{uoBrno}

			  , #{itemCd}
			  , #{ctgryCd}
			  , #{clsfCd}

			  , #{trmtAmt}
			  , #{consignTrmtAmt}

			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)

mapper.delete:

DELETE FROM TB_EV_ONLN_WHLSL_MRKT_DTL	/* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnOnlnWhlslMrktMapper.deleteOnlnDtl */
 WHERE 1=1
   AND YR			= #{yr}
   AND BRNO			= #{brno}
   AND UO_BRNO		= #{uoBrno}
   AND ITEM_CD		= #{itemCd}
