mapper.insert:

    	MERGE INTO TB_EV_UO_ALL_PRCHS_SLS CLT  /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnPurSalMngMapper.insertPrdcrCrclOgnPurSalMng */
		USING DUAL
			ON (
					CLT.APO_CD   = #{apoCd}
				AND CLT.CTGRY_CD = #{ctgryCd}
				AND CLT.ITEM_CD	 = #{itemCd}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        PRCHS_TRST_VLM			= #{prchsTrstVlm}
		    	        , PRCHS_TRST_AMT		= #{prchsTrstAmt}
		    	        , PRCHS_EMSPAP_VLM		= #{prchsEmspapVlm}
		    	        , PRCHS_EMSPAP_AMT		= #{prchsEmspapAmt}

		    	        , SLS_EMSPAP_VLM		= #{slsEmspapVlm}
		    	        , SLS_EMSPAP_AMT		= #{slsEmspapAmt}
		    	        , SLS_TRST_VLM			= #{slsTrstVlm}
		    	        , SLS_TRST_AMT			= #{slsTrstAmt}

		    	        , RMRK					= #{rmrk}

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					 AND CLT.APO_CD		= #{apoCd}
					 AND CLT.APO_SE		= #{apoSe}
					 AND CLT.CTGRY_CD	= #{ctgryCd}
					 AND CLT.ITEM_CD	= #{itemCd}
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR

		    		  , CTGRY_CD
		    		  , ITEM_CD

		    		  , PRCHS_TRST_VLM
		    		  , PRCHS_TRST_AMT
		    		  , PRCHS_EMSPAP_VLM
		    		  , PRCHS_EMSPAP_AMT

		    		  , SLS_EMSPAP_VLM
		    		  , SLS_EMSPAP_AMT
		    		  , SLS_TRST_VLM
		    		  , SLS_TRST_AMT

		    		  , RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , (case when #{apoSe} is not null then #{apoSe}
		   			  			ELSE (SELECT OGNZ_SE_CD FROM TB_APC_OGNZ_INFO WHERE OGNZ_CD = #{apoCd})
		   			  			END)
		   			  , #{brno}
		   			  , (case when #{crno} is not null then #{crno}
		   			  			ELSE (SELECT CRNO FROM TB_EV_APO WHERE APO_CD = #{apoCd})
		   			  			END)
		   			  , #{yr}

		   			  , (case when #{ctgryCd} is not null then #{ctgryCd}
		   			  			ELSE (SELECT CTGRY_CD FROM TB_EV_GPC_CD WHERE ITEM_CD = #{itemCd})
		   			  			END)
		   			  , #{itemCd}

		   			  , #{prchsTrstVlm}
		   			  , #{prchsTrstAmt}
		   			  , #{prchsEmspapVlm}
		   			  , #{prchsEmspapAmt}

		   			  , #{slsEmspapVlm}
		   			  , #{slsEmspapAmt}
		   			  , #{slsTrstVlm}
		   			  , #{slsTrstAmt}

		   			  , #{rmrk}

		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)

mapper.update:

    	UPDATE /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnPurSalMngMapper.upatePrdcrCrclOgnPurSalMng */
    		TB_PD_PrdcrCrclOgnVluIdctrMng
		   SET
		 	  AA	= #{aa}
		 	, BB 	= #{bb}
		 	, CC 	= #{cc}
		 	, CC_CODE 	= #{ccCode}
		 	, DD 	= #{dd}
		 	, DD_CODE 	= #{ddCode}
		 	, EE 	= #{ee}
		 	, FF 	= #{ff}
		 	, GG 	= #{gg}
