mapper.select:
SELECT /* com.at.apcss.pd.pcom.mapper.PrdcrCrclOgnPurSalMngMapper.selectRawDataList */ YR , BRNO , (SELECT CORP_NM FROM TB_EV_APO WHERE BRNO = A.BRNO) AS CORP_NM , DECODE( (SELECT APRV FROM TB_EV_APO WHERE BRNO = A.BRNO ) , '1' , '승인형' , '2' , '육성형' , '' ) AS APRV , PRCHS_SLS_SE , DECODE(A.PRCHS_SLS_SE,'1','매입',2,'매출','') AS PRCHS_SLS_SE_NM , STTG_UPBR_ITEM_SE , (SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'STTG_UPBR_ITEM_SE_1' AND CD_VL = A.STTG_UPBR_ITEM_SE) AS STTG_UPBR_ITEM_NM , ITEM_CD , (SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = A.ITEM_CD) AS ITEM_NM , CTGRY_CD , (SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'CTGRY_CD_1' AND CD_VL = A.CTGRY_CD) AS CTGRY_NM /*매입*/ , PRCHS_TRST_VLM , PRCHS_TRST_AMT , PRCHS_EMSPAP_VLM , PRCHS_EMSPAP_AMT , NVL(PRCHS_TRST_VLM , 0) + NVL(PRCHS_EMSPAP_VLM , 0) AS PRCHS_TOT_VLM , NVL(PRCHS_EMSPAP_AMT , 0) + NVL(PRCHS_EMSPAP_AMT , 0) AS ORG_PRCHS_TOT_AMT , DECODE( A.PRCHS_SLS_SE , '1' , (SELECT SUM( NVL( SLS_CNSGN_PRCHS_AMT , 0 ) ) AS PRCHS_TOT_AMT FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT PST WHERE 1=1 AND APO_SE = '1' AND TYPE_SE_NO IN ('1','3','5','7') AND ( (PRDCR_OGNZ_SN IN (SELECT PRDCR_OGNZ_SN FROM TB_EV_FRMHS_APO EFA WHERE 1=1 AND STBLT_YN = 'Y' AND NVL(EXCL_YN,'N') != 'Y' AND APO_SE = '1' AND BRNO = PST.BRNO AND YR = PST.YR AND ITEM_CD IN (SELECT ITEM_CD FROM TB_EV_APO_YR_GPC_CD WHERE 1=1 AND BRNO = EFA.BRNO AND YR = EFA.YR ) ) ) OR (PRDCR_OGNZ_SN = '0') ) AND ( (ISO_BRNO IN (SELECT BRNO FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '2' AND STBLT_YN = 'Y' AND UO_BRNO = A.BRNO AND YR = A.YR GROUP BY BRNO ) ) OR (ISO_BRNO = '0') ) AND BRNO = A.BRNO AND YR = A.YR AND ITEM_CD = A.ITEM_CD ) ,NVL(PRCHS_EMSPAP_AMT , 0) + NVL(PRCHS_EMSPAP_AMT , 0) ) AS PRCHS_TOT_AMT
/*매출*/ , SLS_EMSPAP_VLM , SLS_EMSPAP_AMT , SLS_TRST_VLM , SLS_TRST_AMT
, NVL(SLS_EMSPAP_VLM , 0) + NVL(SLS_TRST_VLM , 0) AS SLS_TOT_VLM , NVL(SLS_EMSPAP_AMT , 0) + NVL(SLS_TRST_AMT , 0) AS ORG_SLS_TOT_AMT , DECODE( A.PRCHS_SLS_SE , '2' , (SELECT SUM( NVL( SLS_CNSGN_SLS_AMT , 0 ) ) FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '1' AND TYPE_SE_NO IN ('2','3','6','7') AND BRNO = A.BRNO AND YR = A.YR AND ITEM_CD = A.ITEM_CD) , NVL(SLS_EMSPAP_AMT , 0) + NVL(SLS_TRST_AMT , 0) )AS SLS_TOT_AMT
, DDC_VLM , DDC_AMT
/*출하처별 출하실적 명세*/ , PBLC_WHLSL_MRKT_VLM , PBLC_WHLSL_MRKT_AMT , ONLN_WHLSL_MRKT_VLM , ONLN_WHLSL_MRKT_AMT , LGSZ_RTL_VLM , LGSZ_RTL_AMT , ARMY_DLVGDS_VLM , ARMY_DLVGDS_AMT , EATOUT_MTRL_MLSR_VLM , EATOUT_MTRL_MLSR_AMT , MNFC_RTL_VLM , MNFC_RTL_AMT , EXPRT_VLM , EXPRT_AMT , ONLN_DLNG_PRFMNC_VLM , ONLN_DLNG_PRFMNC_AMT , ETC_VLM , ETC_AMT
FROM TB_EV_UO_ISO_ALL_PRCHS_SLS A WHERE APO_SE ='1' AND YR = #{yr} AND BRNO NOT IN (SELECT BRNO FROM TB_COM_USER WHERE USER_ID IN ('at11','at12','at13','at14','at15','at16','at17','at51') ) AND ITEM_CD = DECODE(A.STTG_UPBR_ITEM_SE , '3' , A.ITEM_CD , (SELECT ITEM_CD FROM TB_EV_APO_YR_GPC_CD WHERE BRNO = A.BRNO AND YR = A.YR AND ITEM_CD = A.ITEM_CD) ) ORDER BY BRNO , PRCHS_SLS_SE , STTG_UPBR_ITEM_SE , ITEM_CD
