mapper.insert:

MERGE INTO TB_EV_ACTVTN_FUND_COMPUT_WAY CLT  /* com.at.apcss.pd.pcorm.mapper.PrdcrCrclOgnVluFndsMngMapper.insertComputWayList */
USING DUAL
	ON (
			CLT.YR		= #{yr}
		AND CLT.SEQ		= #{seq}
		)
WHEN MATCHED THEN
		 UPDATE
			   SET
				  COMPUT_WAY_CD			= #{computWayCd}
				, COMPUT_WAY_DTL		= #{computWayDtl}
				, FUND_ARTCL			= #{fundArtcl}
				, ALT					= #{alt}
				, USE_YN				= #{useYn}

				, RMRK					= #{rmrk}

				, SYS_LAST_CHG_DT		= SYSDATE
				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



			WHERE 1=1
			  AND CLT.YR		= #{yr}
			  AND CLT.SEQ		= #{seq}

WHEN NOT MATCHED THEN
	 INSERT
			(
				YR
			  , COMPUT_WAY_CD
			  , COMPUT_WAY_DTL
			  , FUND_ARTCL
			  , ALT
			  , USE_YN
			  , RMRK

			  , SYS_FRST_INPT_DT
			  , SYS_FRST_INPT_USER_ID
			  , SYS_FRST_INPT_PRGRM_ID
			  , SYS_LAST_CHG_DT
			  , SYS_LAST_CHG_USER_ID
			  , SYS_LAST_CHG_PRGRM_ID

			)
		VALUES
			(
				#{yr}
			  , #{computWayCd}
			  , #{computWayDtl}
			  , #{fundArtcl}
			  , #{alt}
			  , #{useYn}
			  , #{rmrk}

			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)
