mapper.select:
SELECT /* com.at.apcss.pd.pcorm.mapper.PrdcrCrclOgnVluFndsMngMapper.selectComputWayList */ YR , SEQ , COMPUT_WAY_CD , COMPUT_WAY_DTL , FUND_ARTCL , ALT , RMRK , NVL(USE_YN,'N') AS USE_YN , DEL_YN FROM TB_EV_ACTVTN_FUND_COMPUT_WAY WHERE 1=1 AND YR = #{yr}
AND FUND_ARTCL = #{fundArtcl}
AND USE_YN = #{useYn}
ORDER BY FUND_ARTCL , COMPUT_WAY_CD

mapper.insert:

MERGE INTO TB_EV_ACTVTN_FUND_COMPUT_WAY CLT  /* com.at.apcss.pd.pcorm.mapper.PrdcrCrclOgnVluFndsMngMapper.insertComputWay */
USING DUAL
	ON (
			CLT.YR		= #{yr}
		AND CLT.SEQ		= #{seq}
		)
WHEN MATCHED THEN
		 UPDATE
			   SET
				  COMPUT_WAY_CD			= #{computWayCd}
				, COMPUT_WAY_DTL		= #{computWayDtl}
				, FUND_ARTCL			= #{fundArtcl}
				, ALT					= #{alt}
				, USE_YN				= #{useYn}

				, RMRK					= #{rmrk}

				, SYS_LAST_CHG_DT		= SYSDATE
				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



			WHERE 1=1
			  AND CLT.YR		= #{yr}
			  AND CLT.SEQ		= #{seq}

WHEN NOT MATCHED THEN
	 INSERT
			(
				YR
			  , COMPUT_WAY_CD
			  , COMPUT_WAY_DTL
			  , FUND_ARTCL
			  , ALT
			  , USE_YN
			  , RMRK

			  , SYS_FRST_INPT_DT
			  , SYS_FRST_INPT_USER_ID
			  , SYS_FRST_INPT_PRGRM_ID
			  , SYS_LAST_CHG_DT
			  , SYS_LAST_CHG_USER_ID
			  , SYS_LAST_CHG_PRGRM_ID

			)
		VALUES
			(
				#{yr}
			  , #{computWayCd}
			  , #{computWayDtl}
			  , #{fundArtcl}
			  , #{alt}
			  , #{useYn}
			  , #{rmrk}

			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)


MERGE INTO TB_EV_ACTVTN_FUND CLT  /* com.at.apcss.pd.pcorm.mapper.PrdcrCrclOgnVluFndsMngMapper.insertActvtnFund */
USING DUAL
	ON (
			CLT.YR		= #{yr}
		AND CLT.SEQ		= #{seq}
		)
WHEN MATCHED THEN
		 UPDATE
			   SET
				  COMPUT_WAY_CD			= #{computWayCd}
				, COMPUT_WAY_RSLT		= #{computWayRslt}
				, FUND_ARTCL			= #{fundArtcl}
				, ALT					= #{alt}
				, SCR					= #{scr}

				, RMRK					= #{rmrk}

				, SYS_LAST_CHG_DT		= SYSDATE
				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



			WHERE 1=1
			  AND CLT.YR		= #{yr}
			  AND CLT.SEQ		= #{seq}

WHEN NOT MATCHED THEN
	 INSERT
			(
				YR
			  , BRNO
			  , COMPUT_WAY_CD
			  , COMPUT_WAY_RSLT
			  , FUND_ARTCL
			  , ALT
			  , SCR
			  , RMRK

			  , SYS_FRST_INPT_DT
			  , SYS_FRST_INPT_USER_ID
			  , SYS_FRST_INPT_PRGRM_ID
			  , SYS_LAST_CHG_DT
			  , SYS_LAST_CHG_USER_ID
			  , SYS_LAST_CHG_PRGRM_ID

			)
		VALUES
			(
				#{yr}
			  , #{brno}
			  , #{computWayCd}
			  , #{computWayRslt}
			  , #{fundArtcl}
			  , #{alt}
			  , #{scr}
			  , #{rmrk}

			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)
