, TOTA.SLS_CNSGN_SLS_AMT AS SLS_CNSGN_SLS_AMT
, TOTB.SLS_CNSGN_SLS_AMT AS SLS_CNSGN_SLS_AMT_TOT
, ROUND(NVL( TOTA.SLS_CNSGN_SLS_AMT / NULLIF( TOTB.SLS_CNSGN_SLS_AMT , 0 ) , 0 ) * 100 , 2 ) AS SLS_CNSGN_SLS_AMT_RT , (SELECT STBLT_YN FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '1' AND TYPE_SE_NO ='8' AND BRNO = ITEM.BRNO AND YR = ITEM.YR AND ITEM_CD = ITEM.ITEM_CD ) AS STBLT_YN FROM (SELECT ITEM_CD , YR , STTG_UPBR_ITEM_SE , CTGRY_CD , TO_CHAR(BRNO) AS BRNO ,(SELECT ITEM_NM FROM TB_ITEM_CRTR WHERE ITEM_CD = AYGC.ITEM_CD) AS ITEM_NM ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'CTGRY_CD' AND CD_VL = AYGC.CTGRY_CD) AS CTGRY_NM ,(SELECT CD_VL_NM FROM TB_COM_CD_DTL WHERE CD_ID = 'STTG_UPBR_ITEM_SE' AND CD_VL = AYGC.STTG_UPBR_ITEM_SE) AS STTG_UPBR_ITEM_NM FROM TB_EV_APO_YR_GPC_CD AYGC WHERE 1=1
AND BRNO = #{brno}
AND YR = #{yr}
) ITEM , (SELECT BRNO , ITEM_CD , SUM( NVL( SLS_CNSGN_SLS_AMT , 0 ) ) AS SLS_CNSGN_SLS_AMT FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '1' AND TYPE_SE_NO IN ('2','6') AND ( (PRDCR_OGNZ_SN IN (SELECT PRDCR_OGNZ_SN FROM TB_EV_FRMHS_APO WHERE 1=1 AND STBLT_YN = 'Y' AND APO_SE = '1'
AND BRNO = #{brno}
AND YR = #{yr}
) )OR (PRDCR_OGNZ_SN = '0') )
AND BRNO = #{brno}
AND YR = #{yr}
GROUP BY BRNO , ITEM_CD ) TOTA , (SELECT BRNO , ITEM_CD , SUM( NVL( SLS_CNSGN_SLS_AMT , 0 ) ) AS SLS_CNSGN_SLS_AMT FROM TB_EV_UO_ISO_PRCHS_PRCHS_SLS_TOT WHERE 1=1 AND APO_SE = '1' AND TYPE_SE_NO IN ('2','3','6','7') AND ( (PRDCR_OGNZ_SN IN (SELECT PRDCR_OGNZ_SN FROM TB_EV_FRMHS_APO WHERE 1=1 AND STBLT_YN = 'Y' AND APO_SE = '1'
AND BRNO = #{brno}
AND YR = #{yr}
) )OR (PRDCR_OGNZ_SN = '0') )
AND BRNO = #{brno}
AND YR = #{yr}
GROUP BY BRNO , ITEM_CD ) TOTB WHERE 1=1 AND ITEM.ITEM_CD = TOTA.ITEM_CD(+) AND ITEM.ITEM_CD = TOTB.ITEM_CD(+) )TOT )TOTAL
