SELECT /* com.at.apcss.pd.pom.mapper.PrdcrOgnCurntMngMapper.selectPrdcrOgnCurntMng */ CM.APO_CD , CM.APO_SE , CM.CTPV , CM.SGG , CM.CORP_NM , CM.BRNO , CM.CRNO
, CM.PIC_FLNM , CM.ZIP , CM.LOTNO_ADDR , CM.LOTNO_DTL_ADDR , CM.ROAD_NM_ADDR , CM.ROAD_NM_DTL_ADDR , CM.CORP_SE_CD , CM.CORP_DTL_SE_CD , CM.CORP_FNDN_DAY , CM.INVST_NOPE , CM.INVST_EXPND_FRMER_NOPE , CM.INVST_AMT , CM.FRMER_INVST_AMT , CM.PRDCR_GRP_INVST_AMT , CM.LOCGOV_INVST_AMT , CM.ETC_INVST_AMT , CM.RGLLBR_NOPE , CM.DW_NOPE , CM.DLBRR_NOPE , CM.ISO_HLD_YN , CM.UNTY_YN , CM.UO_BRNO , CM.UO_NM , CM.UNTY_YR , CM.APRV
, EAM.YR , EAM.APLY_DAY , EAM.RAW_MTR_ENSR_SIGUN_CNT , EAM.RAW_MTR_ENSR_CTPV_CNT , EAM.CTPV_NM , EAM.SIGUN_NM , EAM.PRUO_FUND_APLY_AMT , EAM.ISO_FUND_APLY_AMT , EAM.APLY_TRGT_SE
, CM.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , TO_CHAR(CM.SYS_LAST_CHG_DT,'YYYY-MM-DD') AS SYS_LAST_CHG_DT_YMD , CM.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , (SELECT USER_NM FROM TB_COM_USER WHERE USER_ID = CM.SYS_LAST_CHG_USER_ID) AS SYS_LAST_CHG_USER_NM , CM.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_EV_APO CM , TB_EV_APLY_MNG EAM WHERE 1=1 AND CM.BRNO = EAM.BRNO(+)
AND CM.CMPTN_INST = #{cmptnInst}
AND CM.II_CODE = #{ctpv}
AND CM.CORP_SE_CD = #{corpSeCd}
AND CM.CORP_DTL_SE_CD = #{corpDtlSeCd}
AND CM.BRNO = #{brno}
AND CM.CORP_NM LIKE '%' || #{corpNm} || '%'
AND EAM.YR = #{yr}
ORDER BY CM.CORP_NM DESC

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
