mapper.insert:

    	MERGE INTO TB_EV_FRMHS_PRDCTN_EC_SPMT_STTN CLT  /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.insertEvAplyMng */
		USING DUAL
			ON (
					CLT.APO_CD  = #{apoCd}
				 AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
				 AND CLT.CLTVTN_LAND_SN	= #{cltvtnLandSn}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        CLTVTN_SFC			= #{cltvtnSfc}
		    	        , AVG_YR_PRDCTN_VLM		= #{avgYrPrdctnVlm}
		    	        , JOIN_DAY				= #{joinDay}
		    	        , WHDWL_DAY				= #{whdwlDay}
		    	        , CLTVTN_AREA_RMRK		= #{cltvtnAreaRmrk}

		    	        , FLNM		= #{flnm}
		    	        , CLTVTN_LAND_ADDR		= #{cltvtnLandAddr}

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					 AND CLT.APO_CD			= #{apoCd}
					 AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
					 AND CLT.CLTVTN_LAND_SN	= #{cltvtnLandSn}
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR
		    		  , STTG_UPBR_ITEM_SE
		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , TRMT_TYPE
		    		  , PRDCR_OGNZ_SN
		    		  , PRDCR_OGNZ_CD
		    		  , PRDCR_OGNZ_NM

		    		  , CLTVTN_LAND_SN
		    		  , FLNM
		    		  , CLTVTN_LAND_ADDR
		    		  , CLTVTN_SFC
		    		  , AVG_YR_PRDCTN_VLM
		    		  , JOIN_DAY
		    		  , WHDWL_DAY
		    		  , CLTVTN_AREA_RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , (case when #{apoSe} is not null then #{apoSe}
		   			  			ELSE (SELECT OGNZ_SE_CD FROM TB_APC_OGNZ_INFO WHERE OGNZ_CD = #{apoCd})
		   			  			END)
		   			  , #{brno}
		   			  , (case when #{crno} is not null then #{crno}
		   			  			ELSE (SELECT CRNO FROM TB_EV_APO WHERE APO_CD = #{apoCd})
		   			  			END)
		   			  , #{yr}
		   			  , #{sttgUpbrItemSe}
		   			  , (case when #{ctgryCd} is not null then #{ctgryCd}
		   			  			ELSE (SELECT CTGRY_CD FROM TB_EV_APO_YR_GPC_CD WHERE ITEM_CD = #{itemCd} AND BRNO = #{brno})
		   			  			END)
		   			  , #{itemCd}
		   			  , #{trmtType}
		   			  , #{prdcrOgnzSn}
		   			  , #{prdcrOgnzCd}
		   			  , #{prdcrOgnzNm}

		   			  , SEQ_CLTVTN_LAND_SN.NEXTVAL
		   			  , #{flnm}
		   			  , #{cltvtnLandAddr}

		   			  , #{cltvtnSfc}
		   			  , #{avgYrPrdctnVlm}
		   			  , #{joinDay}
		   			  , #{whdwlDay}
		   			  , #{cltvtnAreaRmrk}

		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)

