mapper.insert:

    	MERGE INTO TB_EV_FRMHS_APO CLT  /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.insertTbEvFrmhsApo */
		USING DUAL
			ON (
					CLT.APO_CD  = #{apoCd}
				AND CLT.BRNO	= #{brno}
				AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
				)
		WHEN MATCHED THEN
				 UPDATE
					   SET
		    	        PRDCR_OGNZ_CD		= #{prdcrOgnzCd}
		    	        , PRDCR_OGNZ_NM		= #{prdcrOgnzNm}
		    	        , RMRK				= #{rmrk}

		    	        , ITEM_CD				= #{itemCd}
		    	        , CTGRY_CD				= #{ctgryCd}

		    	        , PRDCR_OGNZ_CD = FN_GET_PRDCR_OGNZ_CD (#{apoCd} , #{brno} , #{apoSe} , #{prdcrOgnzSn})

		    	        , SYS_LAST_CHG_DT		= SYSDATE
	    				, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    				, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}



					 WHERE 1=1
					 AND CLT.APO_CD			= #{apoCd}
					 AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
		WHEN NOT MATCHED THEN
			 INSERT
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR
		    		  , STTG_UPBR_ITEM_SE
		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , TRMT_TYPE

		    		  , PRDCR_OGNZ_SN
		    		  , PRDCR_OGNZ_CD
		    		  , PRDCR_OGNZ_NM
		    		  , RMRK

		    		  , SYS_FRST_INPT_DT
		    		  , SYS_FRST_INPT_USER_ID
		    		  , SYS_FRST_INPT_PRGRM_ID
		    		  , SYS_LAST_CHG_DT
		    		  , SYS_LAST_CHG_USER_ID
		    		  , SYS_LAST_CHG_PRGRM_ID
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , (case when #{apoSe} is not null then #{apoSe}
		   			  			ELSE (SELECT OGNZ_SE_CD FROM TB_APC_OGNZ_INFO WHERE OGNZ_CD = #{apoCd})
		   			  			END)
		   			  , #{brno}
		   			  , #{crno}
		   			  , #{yr}
		   			  , #{sttgUpbrItemSe}
		   			  , #{ctgryCd}
		   			  , #{itemCd}
		   			  , #{trmtType}

		   			  , SEQ_PRDCR_OGNZ_SN.NEXTVAL
		   			  , FN_GET_PRDCR_OGNZ_CD (#{apoCd} , #{brno} , #{apoSe} , SEQ_PRDCR_OGNZ_SN.CURRVAL+1)
		   			  , #{prdcrOgnzNm}

		   			  , #{rmrk}
		   			  , SYSDATE
	   			  	  , #{sysFrstInptUserId}
		   			  , #{sysFrstInptPrgrmId}
		   			  , SYSDATE
		   			  , #{sysLastChgUserId}
		   			  , #{sysLastChgPrgrmId}
		   			)

mapper.delete:

DELETE FROM TB_EV_FRMHS_APO
 WHERE APO_CD			= #{apoCd}
   AND PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
