'1' THEN NVL(TEFA.BRNO,'') ELSE NVL(TEFA.UO_BRNO,'') END) ) AS APRV ,CNT ,TEFPSS.SPMT_PRC_TOT ,TEFPSS.PRDCTN_VLM_TOT ,TEFPSS.EC_SPMT_VLM_TOT ,TEFPSS.EC_SPMT_PLAN_VLM_TOT ,ROUND(NVL( TEFPSS.EC_SPMT_VLM_TOT/ NULLIF( TEFPSS.PRDCTN_VLM_TOT, 0 ) , 0 ) * 100 , 2 ) AS EC_SPMT_RATE_A ,ROUND(NVL( TEFPSS.EC_SPMT_VLM_TOT/ NULLIF( TEFPSS.EC_SPMT_PLAN_VLM_TOT, 0 ) , 0 ) * 100 , 2 ) AS EC_SPMT_RATE_B FROM (SELECT * FROM TB_EV_FRMHS_APO WHERE 1=1
AND PRDCR_OGNZ_SN = #{prdcrOgnzSn}
AND YR = #{yr}
) TEFA LEFT JOIN (SELECT PRDCR_OGNZ_SN ,YR ,BRNO ,COUNT(PRDCR_OGNZ_SN) AS CNT ,SUM(NVL(SPMT_PRC,0)) AS SPMT_PRC_TOT ,SUM(NVL(PRDCTN_VLM,0)) AS PRDCTN_VLM_TOT ,SUM(NVL(EC_SPMT_VLM,0)) AS EC_SPMT_VLM_TOT ,SUM(NVL(EC_SPMT_PLAN_VLM,0)) AS EC_SPMT_PLAN_VLM_TOT FROM TB_EV_FRMHS_PRDCTN_EC_SPMT_STTN WHERE 1=1
AND PRDCR_OGNZ_SN = #{prdcrOgnzSn}
AND YR = #{yr}
GROUP BY PRDCR_OGNZ_SN , YR , BRNO )TEFPSS ON TEFA.PRDCR_OGNZ_SN = TEFPSS.PRDCR_OGNZ_SN AND TEFA.YR = TEFPSS.YR AND TEFA.BRNO = TEFPSS.BRNO)TOTAL

