mapper.insert:

    	MERGE INTO TB_EV_FRMHS_PRDCTN_EC_SPMT_STTN CLT  /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.insertEvAplyMng */
		USING DUAL
			ON (
					CLT.APO_CD  = #{apoCd}
				 AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
				)
		WHEN MATCHED THEN
				 UPDATE /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.insertEvAplyMng */
					   SET
		    	        PRDCR_OGNZ_CD			= #{prdcrOgnzCd}
		    	        , PRDCR_OGNZ_NM			= #{prdcrOgnzNm}
		    	        , RMRK					= #{rmrk}



					 WHERE 1=1
					 AND CLT.APO_CD			= #{apoCd}
					 AND CLT.PRDCR_OGNZ_SN	= #{prdcrOgnzSn}
		WHEN NOT MATCHED THEN
			 INSERT /* com.at.apcss.pd.aom.mapper.PrdcrCrclOgnReqMngMapper.insertEvAplyMng */
		    		(
		    			APO_CD
		    		  , APO_SE
		    		  , BRNO
		    		  , CRNO
		    		  , YR
		    		  , STTG_UPBR_ITEM_SE
		    		  , CTGRY_CD
		    		  , ITEM_CD
		    		  , TRMT_TYPE
		    		  , PRDCR_OGNZ_SN
		    		  , PRDCR_OGNZ_CD
		    		  , PRDCR_OGNZ_NM
		    		  , RMRK
		    		)
		   		VALUES
		   			(
		   			    #{apoCd}
		   			  , (case when #{apoSe} is not null then #{apoSe}
		   			  			ELSE (SELECT OGNZ_SE_CD FROM TB_APC_OGNZ_INFO WHERE OGNZ_CD = #{apoCd})
		   			  			END)
		   			  , #{brno}
		   			  , #{crno}
		   			  , #{yr}
		   			  , #{sttgUpbrItemSe}
		   			  , #{ctgryCd}
		   			  , #{itemCd}
		   			  , #{trmtType}
		   			  , SEQ_PRDCR_OGNZ_SN.NEXTVAL
		   			  , #{prdcrOgnzCd}
		   			  , #{prdcrOgnzNm}
		   			  , #{rmrk}
		   			)

