mapper.update:
UPDATE /* com.at.apcss.pd.pom.mapper.PrdcrOgnGenalTblMngMapper.updateStbltYn */
	TB_EV_FRMHS_APO
   SET
 	  STBLT_YN				= #{stbltYn}
	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

 WHERE 1=1
   AND YR				= #{yr}
   AND BRNO				= #{brno}

   AND UO_BRNO 			= #{uoBrno}

   AND PRDCR_OGNZ_SN 	= #{prdcrOgnzSn}
